import React, { useState, useEffect } from 'react';
import { getEquipes, createEquipe, updateEquipe, deleteEquipe } from '../../services/equipeService';
import '../Inscricoes/Inscricoes_adaptar.css';

const Equipes = () => {
  const [equipes, setEquipes] = useState([]);
  const [currentEquipe, setCurrentEquipe] = useState({ id: null, nome: '', descricao: '', responsavel: '' });
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  useEffect(() => {
    fetchEquipes();
  }, []);

  // Limpar mensagem de sucesso após 3 segundos
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const fetchEquipes = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getEquipes();
      console.log('Equipes carregadas:', data);
      setEquipes(data);
    } catch (error) {
      console.error('Erro ao buscar equipes:', error);
      setError('Não foi possível carregar as equipes. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEquipe({ ...currentEquipe, [name]: value });
  };

  const resetForm = () => {
    setCurrentEquipe({ id: null, nome: '', descricao: '', responsavel: '' });
    setEditing(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      if (editing && currentEquipe.id) {
        console.log('Atualizando equipe:', currentEquipe);
        await updateEquipe(currentEquipe.id, currentEquipe);
        setSuccessMessage('Equipe atualizada com sucesso!');
      } else {
        console.log('Criando nova equipe:', currentEquipe);
        await createEquipe(currentEquipe);
        setSuccessMessage('Equipe criada com sucesso!');
      }
      resetForm();
      fetchEquipes();
    } catch (error) {
      console.error('Erro ao salvar equipe:', error);
      setError(`Falha ao ${editing ? 'atualizar' : 'criar'} a equipe: ${error.response?.data?.error || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (equipe) => {
    console.log('Editando equipe:', equipe);
    // Garantir que todos os campos existam para evitar erro "undefined"
    setCurrentEquipe({
      id: equipe.id,
      nome: equipe.nome || '',
      descricao: equipe.descricao || '',
      responsavel: equipe.responsavel || ''
    });
    setEditing(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta equipe?')) {
      setLoading(true);
      setError(null);
      try {
        console.log('Excluindo equipe ID:', id);
        await deleteEquipe(id);
        setSuccessMessage('Equipe excluída com sucesso!');
        fetchEquipes();
      } catch (error) {
        console.error('Erro ao excluir equipe:', error);
        setError(`Falha ao excluir a equipe: ${error.response?.data?.error || error.message}`);
        setLoading(false);
      }
    }
  };

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2 className="equipes-title">Gerenciamento de Equipes</h2>
      </div>

      {/* Mensagem de erro */}
      {error && (
        <div className="equipes-message error" style={{backgroundColor: '#ffdddd', padding: '10px', marginBottom: '20px', borderRadius: '5px'}}>
          <p>{error}</p>
          <button onClick={() => setError(null)} className="equipes-button equipes-button-danger" style={{marginTop: '10px'}}>
            Fechar
          </button>
        </div>
      )}

      {/* Mensagem de sucesso */}
      {successMessage && (
        <div className="equipes-message success" style={{backgroundColor: '#ddffdd', padding: '10px', marginBottom: '20px', borderRadius: '5px'}}>
          <p>{successMessage}</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="equipes-form">
        <h3>{editing ? 'Editar Equipe' : 'Adicionar Nova Equipe'}</h3>
        
        {/* Campo oculto para o ID */}
        {editing && (
          <input type="hidden" name="id" value={currentEquipe.id || ''} />
        )}
        
        <div className="equipes-form-group">
          <label htmlFor="nome">Nome da Equipe*</label>
          <input 
            type="text" 
            id="nome" 
            name="nome" 
            value={currentEquipe.nome} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="descricao">Descrição</label>
          <input 
            type="text" 
            id="descricao" 
            name="descricao" 
            value={currentEquipe.descricao || ''} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="responsavel">Responsável</label>
          <input 
            type="text" 
            id="responsavel" 
            name="responsavel" 
            value={currentEquipe.responsavel || ''} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-buttons">
          <button 
            type="submit" 
            className="equipes-button equipes-button-primary"
            disabled={loading}
          >
            {loading ? 'Processando...' : (editing ? 'Atualizar' : 'Adicionar')}
          </button>
          <button 
            type="button" 
            onClick={resetForm} 
            className="equipes-button equipes-button-warning"
            disabled={loading}
          >
            Cancelar
          </button>
        </div>
      </form>

      {loading && !equipes.length ? (
        <p>Carregando equipes...</p>
      ) : (
        <table className="equipes-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Responsável</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {equipes.length > 0 ? (
              equipes.map((equipe) => (
                <tr key={equipe.id}>
                  <td>{equipe.id}</td>
                  <td>{equipe.nome}</td>
                  <td>{equipe.descricao || '-'}</td>
                  <td>{equipe.responsavel || '-'}</td>
                  <td className="equipes-buttons">
                    <button 
                      onClick={() => handleEdit(equipe)} 
                      className="equipes-button equipes-button-warning"
                      disabled={loading}
                    >
                      Editar
                    </button>
                    <button 
                      onClick={() => handleDelete(equipe.id)} 
                      className="equipes-button equipes-button-danger"
                      disabled={loading}
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" style={{ textAlign: 'center' }}>Nenhuma equipe encontrada</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Equipes;
