import React, { useState, useEffect } from "react";
import "./Equipes.scss";
import EquipeForm from "./EquipeForm";
import EquipeCard from "./EquipeCard";
import EquipeDetalhes from "./EquipeDetalhes";
import ApiErrorFallback from "../common/ApiErrorFallback";

const Equipes = () => {
  const [equipes, setEquipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [equipeAtual, setEquipeAtual] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  // Buscar equipes do servidor
  useEffect(() => {
    const fetchEquipes = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        
        try {
          const response = await fetch("http://localhost:5000/api/equipes", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          if (response.ok) {
            const data = await response.json();
            setEquipes(data);
            setError(null);
          } else {
            throw new Error("API de equipes não disponível");
          }
        } catch (apiError) {
          console.warn("API de equipes não disponível, usando dados de exemplo", apiError);
          // Usar dados de exemplo para desenvolvimento
          setEquipes([
            { 
              id: 1, 
              nome: "Equipe de Desenvolvimento", 
              descricao: "Responsável pelo desenvolvimento de software",
              dataCriacao: "2023-01-15",
              membros: [
                { id: 1, nome: "Ana Silva", cargo: "Desenvolvedora Frontend" },
                { id: 2, nome: "Carlos Santos", cargo: "Desenvolvedor Backend" }
              ]
            },
            { 
              id: 2, 
              nome: "Equipe de Design", 
              descricao: "Responsável pelo design de interfaces",
              dataCriacao: "2023-02-10",
              membros: [
                { id: 3, nome: "Julia Oliveira", cargo: "UI Designer" },
                { id: 4, nome: "Lucas Pereira", cargo: "UX Designer" }
              ]
            }
          ]);
          setError("API não disponível. Mostrando dados de exemplo.");
        }
      } catch (err) {
        console.error("Erro ao buscar equipes:", err);
        setError("Não foi possível carregar as equipes. Tente novamente mais tarde.");
        // Dados de exemplo já definidos acima
      } finally {
        setLoading(false);
      }
    };

    fetchEquipes();
  }, [refreshKey]);

  const handleNovaEquipe = () => {
    setEquipeAtual(null);
    setShowForm(true);
  };

  const handleEditarEquipe = (equipe) => {
    setEquipeAtual(equipe);
    setShowForm(true);
  };

  const handleFecharForm = () => {
    setShowForm(false);
    setEquipeAtual(null);
  };

  const handleSalvarEquipe = async (dadosEquipe) => {
    try {
      const token = localStorage.getItem("token");
      const url = equipeAtual 
        ? `http://localhost:5000/api/equipes/${equipeAtual.id}` 
        : "http://localhost:5000/api/equipes";
      
      const method = equipeAtual ? "PUT" : "POST";
      
      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(dadosEquipe),
      });

      if (!response.ok) {
        throw new Error(`Falha ao ${equipeAtual ? 'atualizar' : 'criar'} equipe`);
      }

      // Atualiza a lista de equipes
      setRefreshKey(oldKey => oldKey + 1);
      setShowForm(false);
      setEquipeAtual(null);
    } catch (err) {
      console.error("Erro ao salvar equipe:", err);
      alert(`Erro ao ${equipeAtual ? 'atualizar' : 'criar'} equipe. Tente novamente.`);
      
      // Para desenvolvimento, atualiza os dados localmente
      if (!equipeAtual) {
        // Criar nova equipe
        const novaEquipe = {
          id: equipes.length + 1,
          ...dadosEquipe,
          dataCriacao: new Date().toISOString().split('T')[0],
          membros: []
        };
        setEquipes([...equipes, novaEquipe]);
      } else {
        // Atualizar equipe existente
        const equipesAtualizadas = equipes.map(eq => 
          eq.id === equipeAtual.id ? { ...eq, ...dadosEquipe } : eq
        );
        setEquipes(equipesAtualizadas);
      }
      setShowForm(false);
      setEquipeAtual(null);
    }
  };

  const handleExcluirEquipe = async (id) => {
    // Using window.confirm instead of global confirm
    if (!window.confirm("Tem certeza que deseja excluir esta equipe?")) {
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:5000/api/equipes/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Falha ao excluir equipe");
      }

      // Atualiza a lista de equipes
      setRefreshKey(oldKey => oldKey + 1);
    } catch (err) {
      console.error("Erro ao excluir equipe:", err);
      alert("Erro ao excluir equipe. Tente novamente.");
      
      // Para desenvolvimento, remove a equipe localmente
      setEquipes(equipes.filter(eq => eq.id !== id));
    }
  };

  const handleVerDetalhes = (equipe) => {
    setEquipeAtual(equipe);
  };

  const handleFecharDetalhes = () => {
    setEquipeAtual(null);
  };

  // Renderização condicional
  if (loading) {
    return (
      <div className="equipes-container loading">
        <div className="spinner"></div>
        <p>Carregando equipes...</p>
      </div>
    );
  }

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2>Gerenciamento de Equipes</h2>
        <button className="btn-novo" onClick={handleNovaEquipe}>
          <i className="fas fa-plus"></i> Nova Equipe
        </button>
      </div>

      {error && (
        <ApiErrorFallback 
          message={error} 
          onRetry={() => setRefreshKey(old => old + 1)} 
        />
      )}

      {/* Modal para criar/editar equipe */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={handleFecharForm}>×</button>
            <EquipeForm 
              equipe={equipeAtual} 
              onSave={handleSalvarEquipe}
              onCancel={handleFecharForm}
            />
          </div>
        </div>
      )}

      {/* Modal de detalhes da equipe */}
      {equipeAtual && !showForm && (
        <div className="modal-overlay">
          <div className="modal-content modal-large">
            <button className="close-button" onClick={handleFecharDetalhes}>×</button>
            <EquipeDetalhes 
              equipe={equipeAtual}
              onEdit={() => handleEditarEquipe(equipeAtual)}
              onClose={handleFecharDetalhes}
              onRefresh={() => setRefreshKey(oldKey => oldKey + 1)}
            />
          </div>
        </div>
      )}

      {/* Lista de equipes */}
      {equipes.length === 0 ? (
        <div className="empty-state">
          <i className="fas fa-users"></i>
          <p>Nenhuma equipe cadastrada</p>
          <button className="btn-secundario" onClick={handleNovaEquipe}>Criar uma equipe</button>
        </div>
      ) : (
        <div className="equipes-grid">
          {equipes.map(equipe => (
            <EquipeCard 
              key={equipe.id} 
              equipe={equipe} 
              onEdit={() => handleEditarEquipe(equipe)}
              onDelete={() => handleExcluirEquipe(equipe.id)}
              onView={() => handleVerDetalhes(equipe)}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Equipes;
