import React, { useEffect, useRef } from "react";
import "./style/EventoModals.scss";

export const EventoModalAction = ({ open, onClose, onView, onEdit, onDelete, position }) => {
  const modalRef = useRef(null);
  
  // Fechar o modal ao clicar fora dele
  useEffect(() => {
    if (!open) return;
    
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        onClose();
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [open, onClose]);
  
  // Posicionar o modal acima do botão
  useEffect(() => {
    if (!open || !modalRef.current || !position) return;
    
    const modalEl = modalRef.current;
    const rect = modalEl.getBoundingClientRect();
    
    // Ajusta a posição para ficar acima do botão
    modalEl.style.left = `${position.left - (rect.width / 2)}px`;
    modalEl.style.top = `${position.top - rect.height - 15}px`;
    
  }, [open, position]);

  if (!open) return null;

  // Impedir propagação de cliques para fechar o modal quando clicar nele
  const handleStopPropagation = (e) => {
    e.stopPropagation();
  };

  return (
    <div className="evento-modal-action" ref={modalRef} onClick={handleStopPropagation}>
      <button
        className="evento-modal-action-button view"
        onClick={onView}
      >
        Visualizar
      </button>
      <button
        className="evento-modal-action-button edit"
        onClick={onEdit}
      >
        Editar
      </button>
      <button
        className="evento-modal-action-button delete"
        onClick={onDelete}
      >
        Deletar
      </button>
      <button
        className="evento-modal-action-button close"
        onClick={onClose}
      >
        Fechar
      </button>
    </div>
  );
};
