import React from "react";
import "./style/EventoModals.scss";

export const EventoModalAction = ({ open, onClose, onView, onEdit, onDelete }) => {
  if (!open) return null;

  return (
    <div className="evento-modal-action">
      <button
        className="evento-modal-action-button view"
        onClick={onView}
      >
        Visualizar
      </button>
      <button
        className="evento-modal-action-button edit"
        onClick={onEdit}
      >
        Editar
      </button>
      <button
        className="evento-modal-action-button delete"
        onClick={onDelete}
      >
        Deletar
      </button>
      <button
        className="evento-modal-action-button close"
        onClick={onClose}
      >
        Fechar
      </button>
    </div>
  );
};
