import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Inscricoes_adaptar.css";

const Inscricoes = () => {
  const [eventos, setEventos] = useState([]);
  const [equipes, setEquipes] = useState([]);
  const [minhasEquipes, setMinhasEquipes] = useState([]);
  const [minhasInscricoes, setMinhasInscricoes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [usuario, setUsuario] = useState(null);
  const [tipoVisualizacao, setTipoVisualizacao] = useState("eventos"); // eventos ou equipes
  const [equipeSelecionar, setEquipeSelecionar] = useState(null);
  const [mostrarSeletorEquipe, setMostrarSeletorEquipe] = useState(false);
  const [sucessoMensagem, setSucessoMensagem] = useState("");
  const [erroMensagem, setErroMensagem] = useState("");

  // Buscar usuário logado
  useEffect(() => {
    const usuarioStorage = localStorage.getItem("usuario");
    if (usuarioStorage) {
      setUsuario(JSON.parse(usuarioStorage));
    }
  }, []);

  // Função para buscar dados de eventos, equipes e inscrições
  const fetchData = async () => {
    if (!usuario) return;
    
    setLoading(true);
    setSucessoMensagem("");
    setErroMensagem("");
    
    try {
      // Buscar eventos disponíveis
      const eventosRes = await axios.get("http://localhost:5000/api/eventos");
      const eventosAtivos = eventosRes.data.filter(
        evento => evento.status === 'ativo' || evento.ativo === true
      );
      setEventos(eventosAtivos);
      
      // Buscar equipes disponíveis
      const equipesRes = await axios.get("http://localhost:5000/api/equipes");
      
      // Processar as equipes para garantir que membros seja um array
      const equipesProcessadas = equipesRes.data.map(equipe => {
        // Se a API já retornar membros como um array (baseado no membros_lista do backend)
        if (!equipe.membros) {
          // Tenta processar membros_lista se existir
          if (equipe.membros_lista) {
            try {
              equipe.membros = JSON.parse(equipe.membros_lista);
            } catch (e) {
              console.error('Erro ao processar membros_lista:', e);
              equipe.membros = [];
            }
          } else {
            equipe.membros = [];
          }
        }
        return equipe;
      });
      
      // Filtrar equipes ativas
      const equipesAtivas = equipesProcessadas.filter(
        equipe => equipe.ativo === true || equipe.ativo === undefined
      );
      setEquipes(equipesAtivas);
      
      // Buscar minhas inscrições
      const inscricoesRes = await axios.get(
        `http://localhost:5000/api/inscricoes/usuario/${usuario.id}`
      );
      setMinhasInscricoes(inscricoesRes.data);
      
      // Identificar equipes que o usuário é membro
      const minhasEquipesArray = equipesProcessadas.filter(equipe => 
        Array.isArray(equipe.membros) && equipe.membros.some(membro => {
          if (typeof membro === 'string') {
            // Se membros forem strings simples
            return membro.toLowerCase() === usuario.nome.toLowerCase();
          } else if (membro && typeof membro === 'object') {
            // Se membros forem objetos
            return membro.id === usuario.id || 
                  (membro.nome && membro.nome.toLowerCase() === usuario.nome.toLowerCase()) ||
                  (membro.email && membro.email.toLowerCase() === usuario.email.toLowerCase());
          }
          return false;
        })
      );
      
      setMinhasEquipes(minhasEquipesArray);
      setError(null);
    } catch (err) {
      console.error("Erro ao buscar dados:", err);
      setError("Não foi possível carregar os dados. Por favor, tente novamente mais tarde.");
    } finally {
      setLoading(false);
    }
  };

  // Buscar dados de eventos, equipes e inscrições
  useEffect(() => {
    if (usuario) {
      fetchData();
    }
  }, [usuario]);

  // Função para inscrever equipe em um evento
  const inscreverEquipeEmEvento = async (eventoId, equipeId) => {
    if (!usuario) {
      setErroMensagem("Você precisa estar logado para inscrever uma equipe em um evento.");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/inscricoes", {
        usuario_id: usuario.id,
        evento_id: eventoId,
        equipe_id: equipeId,
        tipo: "evento",
        status: "pendente",
        data_inscricao: new Date().toISOString().split('T')[0]
      });
      
      // Atualizar a lista de inscrições
      const inscricoesRes = await axios.get(
        `http://localhost:5000/api/inscricoes/usuario/${usuario.id}`
      );
      setMinhasInscricoes(inscricoesRes.data);
      setMostrarSeletorEquipe(false);
      
      setSucessoMensagem("Inscrição da equipe realizada com sucesso!");
      setTimeout(() => setSucessoMensagem(""), 3000);
    } catch (err) {
      console.error("Erro ao realizar inscrição da equipe:", err);
      setErroMensagem("Não foi possível realizar a inscrição da equipe. Por favor, tente novamente.");
      setTimeout(() => setErroMensagem(""), 3000);
    }
  };

  // Função para cancelar uma inscrição
  const cancelarInscricao = async (inscricaoId) => {
    try {
      await axios.delete(`http://localhost:5000/api/inscricoes/${inscricaoId}`);
      
      // Atualizar a lista de inscrições
      const inscricoesRes = await axios.get(
        `http://localhost:5000/api/inscricoes/usuario/${usuario.id}`
      );
      setMinhasInscricoes(inscricoesRes.data);
      
      setSucessoMensagem("Inscrição cancelada com sucesso!");
      setTimeout(() => setSucessoMensagem(""), 3000);
    } catch (err) {
      console.error("Erro ao cancelar inscrição:", err);
      setErroMensagem("Não foi possível cancelar a inscrição. Por favor, tente novamente.");
      setTimeout(() => setErroMensagem(""), 3000);
    }
  };

  // Verificar se a equipe já está inscrita em um evento
  const verificarInscricaoEquipe = (eventoId, equipeId) => {
    return minhasInscricoes.some(
      inscricao => inscricao.evento_id === eventoId && inscricao.equipe_id === equipeId
    );
  };

  // Encontrar a inscrição por id de evento e equipe
  const encontrarInscricaoEquipe = (eventoId, equipeId) => {
    return minhasInscricoes.find(
      inscricao => inscricao.evento_id === eventoId && inscricao.equipe_id === equipeId
    );
  };

  // Formatar data
  const formatarData = (dataString) => {
    const data = new Date(dataString);
    return data.toLocaleDateString('pt-BR', { 
      day: '2-digit', 
      month: '2-digit', 
      year: 'numeric' 
    });
  };

  // Função para abrir o seletor de equipes
  const abrirSeletorEquipe = (eventoId) => {
    setEquipeSelecionar(eventoId);
    setMostrarSeletorEquipe(true);
  };

  // Renderizar conteúdo condicional baseado em estados de loading e error
  if (loading) return (
    <div className="inscricoes-loading">
      <div className="loading-icon">
        <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <circle cx="12" cy="12" r="10" fill="none" stroke="#ffe0b2" strokeWidth="2" />
          <path d="M12 2a10 10 0 0 1 10 10" stroke="#ff7f27" strokeWidth="2" fill="none">
            <animateTransform 
              attributeName="transform" 
              type="rotate" 
              from="0 12 12" 
              to="360 12 12" 
              dur="1s" 
              repeatCount="indefinite" 
            />
          </path>
        </svg>
      </div>
      <h3>Carregando inscrições</h3>
      <p>Por favor, aguarde um momento...</p>
    </div>
  );

  if (error) return (
    <div className="inscricoes-error">
      <div className="error-icon">
        <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <circle cx="12" cy="12" r="11" fill="#fff3cd" />
          <path d="M12 5a1 1 0 0 1 1 1v7a1 1 0 0 1-2 0V6a1 1 0 0 1 1-1zm0 11a1 1 0 1 1 0 2 1 1 0 0 1 0-2z" fill="#ff7f27" />
        </svg>
      </div>
      <h3>Não foi possível carregar as inscrições</h3>
      <p>{error}</p>
      <button className="retry-button" onClick={() => fetchData()}>
        <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path d="M17.65 6.35a7.95 7.95 0 0 0-11.3 0 8 8 0 0 0 0 11.3 7.95 7.95 0 0 0 11.3 0 8 8 0 0 0 0-11.3zm-1.4 9.9a6 6 0 0 1-8.5-8.5 6 6 0 0 1 8.5 8.5z" fill="currentColor" />
          <path d="M12 6v3l4-4-4-4v3c-4.42 0-8 3.58-8 8 0 1.57.46 3.03 1.24 4.26L6.7 14.8A5.9 5.9 0 0 1 6 12c0-3.31 2.69-6 6-6zm6.76 1.74L17.3 9.2c.44.84.7 1.8.7 2.8 0 3.31-2.69 6-6 6v-3l-4 4 4 4v-3c4.42 0 8-3.58 8-8 0-1.57-.46-3.03-1.24-4.26z" fill="currentColor" />
        </svg>
        Tentar novamente
      </button>
    </div>
  );

  return (
    <div className="inscricoes-container">
      <div className="inscricoes-header">
        <h2>Inscrições</h2>
        <p className="inscricoes-subtitle">Inscreva suas equipes em eventos</p>

        <div className="tipo-visualizacao">
          <button 
            className={`tipo-btn ${tipoVisualizacao === "eventos" ? "ativo" : ""}`}
            onClick={() => setTipoVisualizacao("eventos")}
          >
            Eventos
          </button>
          <button 
            className={`tipo-btn ${tipoVisualizacao === "equipes" ? "ativo" : ""}`}
            onClick={() => setTipoVisualizacao("equipes")}
          >
            Minhas Equipes
          </button>
        </div>
      </div>

      {/* Mensagens de sucesso e erro */}
      {sucessoMensagem && (
        <div className="mensagem-sucesso">
          <span>✅</span> {sucessoMensagem}
        </div>
      )}
      {erroMensagem && (
        <div className="mensagem-erro">
          <span>❌</span> {erroMensagem}
        </div>
      )}

      {/* Seção de seleção de equipe para inscrição */}
      {mostrarSeletorEquipe && (
        <div className="modal-overlay">
          <div className="modal-container equipe-selector">
            <div className="modal-header">
              <h2>Selecionar Equipe</h2>
              <button className="close-button" onClick={() => setMostrarSeletorEquipe(false)}>
                &times;
              </button>
            </div>
            <div className="modal-body">
              <p>Selecione uma equipe para inscrever no evento:</p>
              {minhasEquipes.length === 0 ? (
                <p className="no-data">Você não é membro de nenhuma equipe.</p>
              ) : (
                <div className="equipes-lista">
                  {minhasEquipes.map((equipe) => (
                    <div 
                      key={equipe.id || equipe._id} 
                      className="equipe-item"
                      onClick={() => inscreverEquipeEmEvento(equipeSelecionar, equipe.id || equipe._id)}
                    >
                      <h4>{equipe.nome}</h4>
                      <p className="equipe-tipo">{equipe.tipo || "Tipo não especificado"}</p>
                      <p className="equipe-membros">
                        Membros: {Array.isArray(equipe.membros) ? equipe.membros.length : 0}
                      </p>
                    </div>
                  ))}
                </div>
              )}
            </div>
            <div className="modal-footer">
              <button 
                className="cancel-button" 
                onClick={() => setMostrarSeletorEquipe(false)}
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Seção de itens disponíveis para inscrição */}
      {tipoVisualizacao === "eventos" ? (
        <div className="inscricoes-section">
          <h3 className="section-title">Eventos Disponíveis</h3>
          
          {eventos.length > 0 ? (
            <div className="itens-grid">
              {eventos.map((evento) => {
                // Verificar se alguma das minhas equipes está inscrita neste evento
                const equipesInscritas = minhasEquipes
                  .filter(equipe => verificarInscricaoEquipe(evento.id || evento._id, equipe.id || equipe._id))
                  .map(equipe => ({
                    equipe,
                    inscricao: encontrarInscricaoEquipe(evento.id || evento._id, equipe.id || equipe._id)
                  }));
                
                return (
                  <div className="item-card" key={evento.id || evento._id}>
                    <div className="item-header">
                      <h4 className="item-titulo">{evento.nome || evento.titulo}</h4>
                      <div className="item-status">
                        <span className="status-badge ativo">Ativo</span>
                      </div>
                    </div>
                    
                    <div className="item-info">
                      <p className="item-data">
                        <strong>Data:</strong> {formatarData(evento.data)}
                      </p>
                      <p className="item-local">
                        <strong>Local:</strong> {evento.local || "Local não informado"}
                      </p>
                      <p className="item-descricao">
                        {evento.descricao || "Sem descrição disponível"}
                      </p>
                    </div>
                    
                    <div className="item-actions">
                      {equipesInscritas.length > 0 ? (
                        <div className="inscricoes-equipes">
                          <h5>Equipes Inscritas:</h5>
                          {equipesInscritas.map(({equipe, inscricao}) => (
                            <div key={equipe.id || equipe._id} className="equipe-inscrita">
                              <div className="equipe-inscrita-info">
                                <span className="equipe-nome">{equipe.nome}</span>
                                <span className={`inscricao-status ${inscricao.status}`}>
                                  {inscricao.status === "aprovado" 
                                    ? "Aprovada"
                                    : inscricao.status === "pendente" 
                                      ? "Pendente" 
                                      : "Recusada"}
                                </span>
                              </div>
                              <button
                                className="cancelar-btn"
                                onClick={() => cancelarInscricao(inscricao.id || inscricao._id)}
                              >
                                Cancelar
                              </button>
                            </div>
                          ))}
                          <button 
                            className="inscrever-btn secondary"
                            onClick={() => abrirSeletorEquipe(evento.id || evento._id)}
                          >
                            Inscrever Outra Equipe
                          </button>
                        </div>
                      ) : (
                        <button 
                          className="inscrever-btn"
                          onClick={() => abrirSeletorEquipe(evento.id || evento._id)}
                          disabled={minhasEquipes.length === 0}
                        >
                          {minhasEquipes.length === 0 
                            ? "Você não é membro de nenhuma equipe" 
                            : "Inscrever Equipe"}
                        </button>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <p className="no-data">Não há eventos disponíveis para inscrição no momento.</p>
          )}
        </div>
      ) : (
        <div className="inscricoes-section">
          <h3 className="section-title">Minhas Equipes</h3>
          
          {minhasEquipes.length > 0 ? (
            <div className="itens-grid">
              {minhasEquipes.map((equipe) => {
                // Buscar inscrições desta equipe
                const inscricoesEquipe = minhasInscricoes.filter(
                  inscricao => inscricao.equipe_id === (equipe.id || equipe._id)
                );
                
                return (
                  <div className="item-card" key={equipe.id || equipe._id}>
                    <div className="item-header">
                      <h4 className="item-titulo">{equipe.nome}</h4>
                      <div className="item-status">
                        <span className="status-badge ativo">Ativa</span>
                      </div>
                    </div>
                    
                    <div className="item-info">
                      <p className="item-tipo">
                        <strong>Tipo:</strong> {equipe.tipo || "Não especificado"}
                      </p>
                      <p className="item-membros">
                        <strong>Membros:</strong> {Array.isArray(equipe.membros) ? equipe.membros.length : 0}
                      </p>
                      <p className="item-descricao">
                        {equipe.descricao || "Sem descrição disponível"}
                      </p>
                    </div>
                    
                    <div className="item-actions">
                      <h5>Inscrições em Eventos:</h5>
                      {inscricoesEquipe.length > 0 ? (
                        <div className="inscricoes-eventos-lista">
                          {inscricoesEquipe.map(inscricao => (
                            <div key={inscricao.id || inscricao._id} className="inscricao-evento-item">
                              <div className="inscricao-evento-info">
                                <span className="evento-nome">{inscricao.evento_nome}</span>
                                <span className={`inscricao-status ${inscricao.status}`}>
                                  {inscricao.status === "aprovado" 
                                    ? "Aprovada"
                                    : inscricao.status === "pendente" 
                                      ? "Pendente" 
                                      : "Recusada"}
                                </span>
                              </div>
                              <button
                                className="cancelar-btn"
                                onClick={() => cancelarInscricao(inscricao.id || inscricao._id)}
                              >
                                Cancelar
                              </button>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <p className="no-inscricoes">Nenhuma inscrição em eventos.</p>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <p className="no-data">Você não é membro de nenhuma equipe.</p>
          )}
        </div>
      )}

      {/* Seção de minhas inscrições */}
      <div className="inscricoes-section">
        <h3 className="section-title">Inscrições das Minhas Equipes</h3>
        
        {minhasInscricoes.length > 0 ? (
          <div className="minhas-inscricoes">
            <table className="inscricoes-tabela">
              <thead>
                <tr>
                  <th>Evento</th>
                  <th>Equipe</th>
                  <th>Data de Inscrição</th>
                  <th>Status</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {minhasInscricoes.map((inscricao) => (
                  <tr key={inscricao.id || inscricao._id}>
                    <td>{inscricao.evento_nome || "Evento não encontrado"}</td>
                    <td>{inscricao.equipe_nome || "Equipe não encontrada"}</td>
                    <td>{formatarData(inscricao.data_inscricao)}</td>
                    <td>
                      <span className={`status-badge ${inscricao.status}`}>
                        {inscricao.status === "aprovado" 
                          ? "Aprovado" 
                          : inscricao.status === "pendente" 
                            ? "Pendente" 
                            : inscricao.status === "recusado" 
                              ? "Recusado" 
                              : inscricao.status}
                      </span>
                    </td>
                    <td>
                      <button 
                        className="cancelar-btn pequeno"
                        onClick={() => cancelarInscricao(inscricao.id || inscricao._id)}
                      >
                        Cancelar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p className="no-data">Suas equipes ainda não possuem inscrições em eventos.</p>
        )}
      </div>
    </div>
  );
};

export default Inscricoes;
