import React, { useState, useEffect } from 'react';
import { 
  getEquipes, 
  createEquipe, 
  updateEquipe, 
  deleteEquipe, 
  checkServerStatus,
  getServerStatusObject,
  verificarVagasEquipe
} from '../../services/equipeService';
import './Equipes.css';

const Equipes = () => {
  const [equipes, setEquipes] = useState([]);
  const [currentEquipe, setCurrentEquipe] = useState({ 
    id: null, 
    nome: '', 
    descricao: '', 
    responsavel: '',
    max_participantes: 10
  });
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [serverStatus, setServerStatus] = useState({ online: false, message: 'Verificando conexão...' });
  const [submitting, setSubmitting] = useState(false);

  // Verificar status do servidor e buscar equipes ao carregar
  useEffect(() => {
    const checkServer = async () => {
      try {
        const status = await checkServerStatus(true); // forçar verificação
        setServerStatus(status);
      } catch (error) {
        console.error('Erro ao verificar status do servidor:', error);
        setServerStatus({ online: false, message: 'Não foi possível verificar o status do servidor' });
      }
    };
    
    checkServer();
    fetchEquipes();
    
    // Monitorar status do servidor a cada 30 segundos
    const statusInterval = setInterval(() => {
      setServerStatus(getServerStatusObject());
    }, 30000);
    
    return () => clearInterval(statusInterval);
  }, []);

  // Limpar mensagem de sucesso após 3 segundos
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const fetchEquipes = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getEquipes();
      setEquipes(data);
    } catch (error) {
      console.error('Erro ao buscar equipes:', error);
      
      if (error.isConnectionError) {
        setError('Não foi possível conectar ao servidor. Verifique se o servidor está rodando.');
      } else {
        setError(error.customMessage || 'Não foi possível carregar as equipes. Por favor, tente novamente.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEquipe({ ...currentEquipe, [name]: value });
  };

  const resetForm = () => {
    setCurrentEquipe({ 
      id: null, 
      nome: '', 
      descricao: '', 
      responsavel: '',
      max_participantes: 10
    });
    setEditing(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Impedir múltiplos envios
    if (submitting) return;
    
    setSubmitting(true);
    setLoading(true);
    setError(null);
    
    try {
      // Verificar conexão antes de tentar salvar
      const status = await checkServerStatus(true);
      
      if (!status.online) {
        throw new Error('O servidor está offline. Não é possível salvar a equipe.');
      }
      
      if (editing && currentEquipe.id) {
        await updateEquipe(currentEquipe.id, currentEquipe);
        setSuccessMessage('Equipe atualizada com sucesso!');
      } else {
        await createEquipe(currentEquipe);
        setSuccessMessage('Equipe criada com sucesso!');
      }
      
      resetForm();
      fetchEquipes();
    } catch (error) {
      console.error('Erro ao salvar equipe:', error);
      
      // Verificar o status do servidor após um erro
      try {
        const currentStatus = await checkServerStatus(true);
        setServerStatus(currentStatus);
      } catch (statusError) {
        console.error('Erro ao verificar status do servidor após falha:', statusError);
      }
      
      setError(error.customMessage || error.message || `Falha ao ${editing ? 'atualizar' : 'criar'} a equipe.`);
    } finally {
      setLoading(false);
      setSubmitting(false);
    }
  };

  const handleEdit = (equipe) => {
    setCurrentEquipe({
      id: equipe.id,
      nome: equipe.nome || '',
      descricao: equipe.descricao || '',
      responsavel: equipe.responsavel || '',
      max_participantes: equipe.max_participantes || 10
    });
    setEditing(true);
  };

  const handleDelete = async (id) => {
    // Verificar conexão antes de tentar excluir
    try {
      const status = await checkServerStatus(true);
      
      if (!status.online) {
        setError('O servidor está offline. Não é possível excluir a equipe.');
        return;
      }
    } catch (error) {
      setError('Não foi possível verificar a conexão com o servidor.');
      return;
    }
    
    if (window.confirm('Tem certeza que deseja excluir esta equipe?')) {
      setLoading(true);
      setError(null);
      
      try {
        await deleteEquipe(id);
        setSuccessMessage('Equipe excluída com sucesso!');
        fetchEquipes();
      } catch (error) {
        console.error('Erro ao excluir equipe:', error);
        setError(error.customMessage || error.message || 'Falha ao excluir a equipe.');
      } finally {
        setLoading(false);
      }
    }
  };

  const retryConnection = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const status = await checkServerStatus(true);
      setServerStatus(status);
      
      if (status.online) {
        setSuccessMessage('Conexão com o servidor estabelecida!');
        fetchEquipes();
      } else {
        setError('Não foi possível conectar ao servidor. Verifique se ele está rodando.');
      }
    } catch (error) {
      setError('Falha ao verificar conexão com o servidor.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2 className="equipes-title">Gerenciamento de Equipes</h2>
        <div className={`server-status ${serverStatus.online ? 'online' : 'offline'}`}>
          {serverStatus.online ? '🟢 Online' : '🔴 Offline'}
          {!serverStatus.online && (
            <button 
              onClick={retryConnection} 
              className="equipes-button equipes-button-primary"
              disabled={loading}
            >
              Reconectar
            </button>
          )}
        </div>
      </div>

      {/* Mensagem de erro */}
      {error && (
        <div className="equipes-message error">
          <p>{error}</p>
          <button 
            onClick={() => setError(null)} 
            className="equipes-button equipes-button-danger"
          >
            Fechar
          </button>
        </div>
      )}

      {/* Mensagem de sucesso */}
      {successMessage && (
        <div className="equipes-message success">
          <p>{successMessage}</p>
        </div>
      )}

      {/* Aviso quando o servidor estiver offline */}
      {!serverStatus.online && (
        <div className="equipes-message warning">
          <p>
            <strong>⚠️ Atenção:</strong> O sistema não consegue se conectar ao servidor. 
            Verifique se o servidor está rodando e tente novamente.
          </p>
          <div className="equipes-help-text">
            Dicas:
            <ul>
              <li>Verifique se o servidor backend está em execução</li>
              <li>Verifique se a URL da API está correta</li>
              <li>Tente reiniciar o servidor</li>
            </ul>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="equipes-form">
        <h3>{editing ? 'Editar Equipe' : 'Adicionar Nova Equipe'}</h3>
        
        <div className="equipes-form-group">
          <label htmlFor="nome">Nome da Equipe*</label>
          <input 
            type="text" 
            id="nome" 
            name="nome" 
            value={currentEquipe.nome} 
            onChange={handleInputChange} 
            required 
            disabled={loading || submitting}
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="descricao">Descrição</label>
          <input 
            type="text" 
            id="descricao" 
            name="descricao" 
            value={currentEquipe.descricao || ''} 
            onChange={handleInputChange} 
            disabled={loading || submitting}
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="responsavel">Responsável</label>
          <input 
            type="text" 
            id="responsavel" 
            name="responsavel" 
            value={currentEquipe.responsavel || ''} 
            onChange={handleInputChange} 
            disabled={loading || submitting}
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="max_participantes">Máximo de Participantes</label>
          <input 
            type="number" 
            id="max_participantes" 
            name="max_participantes" 
            min="1"
            value={currentEquipe.max_participantes || 10} 
            onChange={handleInputChange} 
            disabled={loading || submitting}
          />
        </div>

        <div className="equipes-buttons">
          <button 
            type="submit" 
            className="equipes-button equipes-button-primary"
            disabled={loading || submitting || !serverStatus.online}
          >
            {submitting ? 'Enviando...' : (editing ? 'Atualizar' : 'Adicionar')}
          </button>
          <button 
            type="button" 
            onClick={resetForm} 
            className="equipes-button equipes-button-warning"
            disabled={loading || submitting}
          >
            Cancelar
          </button>
        </div>
        
        {!serverStatus.online && (
          <p className="equipes-form-help">
            <strong>Nota:</strong> O botão de {editing ? 'atualização' : 'adição'} está desativado porque 
            o servidor está offline no momento.
          </p>
        )}
      </form>

      {loading && !equipes.length ? (
        <div className="equipes-loading">
          <p>Carregando equipes...</p>
          <div className="loading-spinner"></div>
        </div>
      ) : (
        <div className="equipes-table-container">
          <table className="equipes-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Nome</th>
                <th>Descrição</th>
                <th>Responsável</th>
                <th>Vagas</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {equipes.length > 0 ? (
                equipes.map((equipe) => (
                  <tr key={equipe.id}>
                    <td>{equipe.id}</td>
                    <td>{equipe.nome}</td>
                    <td>{equipe.descricao || '-'}</td>
                    <td>{equipe.responsavel || '-'}</td>
                    <td>{equipe.max_participantes || 10}</td>
                    <td className="equipes-actions">
                      <button 
                        onClick={() => handleEdit(equipe)} 
                        className="equipes-button equipes-button-warning"
                        disabled={loading || submitting || !serverStatus.online}
                        title={!serverStatus.online ? "Servidor offline" : "Editar equipe"}
                      >
                        Editar
                      </button>
                      <button 
                        onClick={() => handleDelete(equipe.id)} 
                        className="equipes-button equipes-button-danger"
                        disabled={loading || submitting || !serverStatus.online}
                        title={!serverStatus.online ? "Servidor offline" : "Excluir equipe"}
                      >
                        Excluir
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="equipes-no-data">
                    {!serverStatus.online 
                      ? "Não foi possível carregar as equipes. O servidor está offline." 
                      : "Nenhuma equipe encontrada."}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Equipes;
