import React, { useEffect, useRef } from "react";
import "./style/EventoModals.scss";

export const EventoModalAction = ({ open, onClose, onView, onEdit, onDelete, position }) => {
  const modalRef = useRef(null);
  
  useEffect(() => {
    if (open && modalRef.current) {
      const modal = modalRef.current;
      
      // Posicionamento padrão no centro se não houver posição específica
      if (!position) return;
      
      modal.style.position = "absolute";
      modal.style.top = `${position.top - 10}px`;
      modal.style.left = `${position.left}px`;
      modal.style.transform = "translateY(-100%)";
    }
  }, [open, position]);

  if (!open) return null;

  return (
    <div className="evento-modal-action" ref={modalRef}>
      <button
        className="evento-modal-action-button view"
        onClick={onView}
      >
        Visualizar
      </button>
      <button
        className="evento-modal-action-button edit"
        onClick={onEdit}
      >
        Editar
      </button>
      <button
        className="evento-modal-action-button delete"
        onClick={onDelete}
      >
        Deletar
      </button>
      <button
        className="evento-modal-action-button close"
        onClick={onClose}
      >
        Fechar
      </button>
    </div>
  );
};
