import React, { useState, useEffect } from 'react';
import { 
  getEquipes, 
  createEquipe, 
  updateEquipe, 
  deleteEquipe, 
  checkServerStatus 
} from '../../services/equipeService';
import '../Inscricoes/Inscricoes_adaptar.css';

const Equipes = () => {
  const [equipes, setEquipes] = useState([]);
  const [currentEquipe, setCurrentEquipe] = useState({ 
    id: null, 
    nome: '', 
    descricao: '', 
    responsavel: '',
    max_participantes: 10
  });
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [serverStatus, setServerStatus] = useState({ online: false, message: 'Verificando conexão...' });
  const [offlineMode, setOfflineMode] = useState(false);

  useEffect(() => {
    const checkConnection = async () => {
      try {
        const status = await checkServerStatus();
        setServerStatus(status);
        setOfflineMode(!status.online);
      } catch (error) {
        console.error('Erro ao verificar status do servidor:', error);
        setServerStatus({ online: false, message: 'Não foi possível verificar o status do servidor' });
        setOfflineMode(true);
      }
    };
    
    checkConnection();
    fetchEquipes();
    
    // Verificar conexão a cada 30 segundos
    const intervalId = setInterval(checkConnection, 30000);
    return () => clearInterval(intervalId);
  }, []);

  // Limpar mensagem de sucesso após 3 segundos
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const fetchEquipes = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getEquipes();
      setEquipes(data);
    } catch (error) {
      console.error('Erro ao buscar equipes:', error);
      setError('Não foi possível carregar as equipes. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEquipe({ ...currentEquipe, [name]: value });
  };

  const resetForm = () => {
    setCurrentEquipe({ 
      id: null, 
      nome: '', 
      descricao: '', 
      responsavel: '',
      max_participantes: 10
    });
    setEditing(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Verificar se está em modo offline
    if (offlineMode) {
      setError("Não é possível salvar dados enquanto estiver offline. Por favor, verifique sua conexão com o servidor.");
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      if (editing && currentEquipe.id) {
        await updateEquipe(currentEquipe.id, currentEquipe);
        setSuccessMessage('Equipe atualizada com sucesso!');
      } else {
        await createEquipe(currentEquipe);
        setSuccessMessage('Equipe criada com sucesso!');
      }
      resetForm();
      fetchEquipes();
    } catch (error) {
      console.error('Erro ao salvar equipe:', error);
      setError(error.customMessage || `Falha ao ${editing ? 'atualizar' : 'criar'} a equipe: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (equipe) => {
    setCurrentEquipe({
      id: equipe.id,
      nome: equipe.nome || '',
      descricao: equipe.descricao || '',
      responsavel: equipe.responsavel || '',
      max_participantes: equipe.max_participantes || 10
    });
    setEditing(true);
  };

  const handleDelete = async (id) => {
    // Verificar se está em modo offline
    if (offlineMode) {
      setError("Não é possível excluir dados enquanto estiver offline. Por favor, verifique sua conexão com o servidor.");
      return;
    }
    
    if (window.confirm('Tem certeza que deseja excluir esta equipe?')) {
      setLoading(true);
      setError(null);
      try {
        await deleteEquipe(id);
        setSuccessMessage('Equipe excluída com sucesso!');
        fetchEquipes();
      } catch (error) {
        console.error('Erro ao excluir equipe:', error);
        setError(error.customMessage || `Falha ao excluir a equipe: ${error.message}`);
      } finally {
        setLoading(false);
      }
    }
  };

  const retryConnection = async () => {
    try {
      setLoading(true);
      const status = await checkServerStatus();
      setServerStatus(status);
      setOfflineMode(!status.online);
      
      if (status.online) {
        fetchEquipes();
        setSuccessMessage('Conexão com o servidor estabelecida!');
      } else {
        setError('Não foi possível conectar ao servidor. O sistema está operando em modo offline.');
      }
    } catch (error) {
      setError('Falha ao verificar conexão com o servidor.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2 className="equipes-title">Gerenciamento de Equipes</h2>
        <div className={`server-status ${serverStatus.online ? 'online' : 'offline'}`}>
          {serverStatus.online ? '🟢 Online' : '🔴 Offline'}
          {!serverStatus.online && (
            <button 
              onClick={retryConnection} 
              className="equipes-button equipes-button-primary"
              style={{ marginLeft: '10px', padding: '4px 8px', fontSize: '12px' }}
              disabled={loading}
            >
              Reconectar
            </button>
          )}
        </div>
      </div>

      {/* Mensagem de erro */}
      {error && (
        <div className="equipes-message error">
          <p>{error}</p>
          <button onClick={() => setError(null)} className="equipes-button equipes-button-danger">
            Fechar
          </button>
        </div>
      )}

      {/* Mensagem de sucesso */}
      {successMessage && (
        <div className="equipes-message success">
          <p>{successMessage}</p>
        </div>
      )}

      {/* Aviso de modo offline */}
      {offlineMode && (
        <div className="equipes-message warning">
          <p>⚠️ O sistema está operando em modo offline. Algumas funcionalidades estarão limitadas até que a conexão com o servidor seja restabelecida.</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="equipes-form">
        <h3>{editing ? 'Editar Equipe' : 'Adicionar Nova Equipe'}</h3>
        
        <div className="equipes-form-group">
          <label htmlFor="nome">Nome da Equipe*</label>
          <input 
            type="text" 
            id="nome" 
            name="nome" 
            value={currentEquipe.nome} 
            onChange={handleInputChange} 
            required 
            disabled={loading || offlineMode}
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="descricao">Descrição</label>
          <input 
            type="text" 
            id="descricao" 
            name="descricao" 
            value={currentEquipe.descricao || ''} 
            onChange={handleInputChange} 
            disabled={loading || offlineMode}
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="responsavel">Responsável</label>
          <input 
            type="text" 
            id="responsavel" 
            name="responsavel" 
            value={currentEquipe.responsavel || ''} 
            onChange={handleInputChange} 
            disabled={loading || offlineMode}
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="max_participantes">Máximo de Participantes</label>
          <input 
            type="number" 
            id="max_participantes" 
            name="max_participantes" 
            min="1"
            value={currentEquipe.max_participantes || 10} 
            onChange={handleInputChange} 
            disabled={loading || offlineMode}
          />
        </div>

        <div className="equipes-buttons">
          <button 
            type="submit" 
            className="equipes-button equipes-button-primary"
            disabled={loading || offlineMode}
          >
            {loading ? 'Processando...' : (editing ? 'Atualizar' : 'Adicionar')}
          </button>
          <button 
            type="button" 
            onClick={resetForm} 
            className="equipes-button equipes-button-warning"
            disabled={loading}
          >
            Cancelar
          </button>
        </div>
      </form>

      {loading && !equipes.length ? (
        <p>Carregando equipes...</p>
      ) : (
        <table className="equipes-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Responsável</th>
              <th>Vagas</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {equipes.length > 0 ? (
              equipes.map((equipe) => (
                <tr key={equipe.id}>
                  <td>{equipe.id}</td>
                  <td>{equipe.nome}</td>
                  <td>{equipe.descricao || '-'}</td>
                  <td>{equipe.responsavel || '-'}</td>
                  <td>{equipe.max_participantes || 10}</td>
                  <td className="equipes-buttons">
                    <button 
                      onClick={() => handleEdit(equipe)} 
                      className="equipes-button equipes-button-warning"
                      disabled={loading || offlineMode}
                    >
                      Editar
                    </button>
                    <button 
                      onClick={() => handleDelete(equipe.id)} 
                      className="equipes-button equipes-button-danger"
                      disabled={loading || offlineMode}
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" style={{ textAlign: 'center' }}>Nenhuma equipe encontrada</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Equipes;
