// src/Modals/EventoModalDelete.jsx
import React from 'react';
import "./style/EventoModals.scss";

const EventoModalDelete = ({ isOpen, onClose, onConfirm, evento }) => {
  if (!isOpen || !evento) return null;

  return (
    <div className="evento-modal-delete">
      <div className="evento-modal-delete-container">
        <div className="evento-modal-delete-header">
          <h2>Confirmar Exclusão</h2>
        </div>
        
        <div className="evento-modal-delete-warning">
          <span className="warning-icon">⚠️</span>
        </div>
        
        <div className="evento-modal-delete-content">
          <p>Tem certeza que deseja excluir o evento <strong>{evento.nome}</strong>?</p>
          <p>Esta ação não pode ser desfeita.</p>
        </div>
        
        <div className="evento-modal-delete-footer">
          <button 
            className="evento-modal-delete-button cancel"
            onClick={onClose}
          >
            Cancelar
          </button>
          <button 
            className="evento-modal-delete-button confirm"
            onClick={() => onConfirm(evento.id)}
          >
            Excluir
          </button>
        </div>
      </div>
    </div>
  );
};

export default EventoModalDelete;
