import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './styles/login.scss';

export const Login = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        rememberMe: false
    });
    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [loginError, setLoginError] = useState(null);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value
        });

        // Clear errors for this field when user types
        if (errors[name]) {
            setErrors({
                ...errors,
                [name]: null
            });
        }
    };

    const validateForm = () => {
        const newErrors = {};

        // Validate email
        if (!formData.email) {
            newErrors.email = "E-mail é obrigatório";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = "E-mail inválido";
        }

        // Validate password
        if (!formData.password) {
            newErrors.password = "Senha é obrigatória";
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return;
        }

        setIsSubmitting(true);
        setLoginError(null);

        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1500));

            // Mock login validation - Replace with actual API call
            if (formData.email === "usuario@exemplo.com" && formData.password === "senha123") {
                // Success - Save user session and redirect
                localStorage.setItem("isLoggedIn", "true");
                localStorage.setItem("user", JSON.stringify({ 
                    name: "Usuário Exemplo", 
                    email: formData.email 
                }));
                
                navigate("/dashboard");
            } else {
                setLoginError("E-mail ou senha incorretos. Por favor, tente novamente.");
                // Add shake animation to form container
                const container = document.querySelector(".login-container");
                container.classList.add("shake");
                
                // Remove animation class after animation ends
                setTimeout(() => {
                    container.classList.remove("shake");
                }, 600);
            }
        } catch (error) {
            console.error("Login error:", error);
            setLoginError("Ocorreu um erro ao fazer login. Por favor, tente novamente.");
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="login-page">
            <div className="login-container">
                <div className="login-header">
                    <h1>Bem-vindo de volta</h1>
                    <p>Entre com sua conta para continuar</p>
                </div>

                <form className="login-form" onSubmit={handleSubmit}>
                    {loginError && (
                        <div className="error-alert">
                            {loginError}
                            <button 
                                type="button" 
                                className="close-button"
                                onClick={() => setLoginError(null)}
                            >
                                &times;
                            </button>
                        </div>
                    )}

                    <div className="form-group">
                        <label htmlFor="email">E-mail</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            placeholder="seu@email.com"
                            className={errors.email ? "error" : ""}
                            disabled={isSubmitting}
                        />
                        {errors.email && (
                            <div className="error-message">{errors.email}</div>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Senha</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            placeholder="Sua senha"
                            className={errors.password ? "error" : ""}
                            disabled={isSubmitting}
                        />
                        {errors.password && (
                            <div className="error-message">{errors.password}</div>
                        )}
                    </div>

                    <div className="remember-forgot">
                        <div className="remember-me">
                            <input
                                type="checkbox"
                                id="rememberMe"
                                name="rememberMe"
                                checked={formData.rememberMe}
                                onChange={handleChange}
                                disabled={isSubmitting}
                            />
                            <label htmlFor="rememberMe">Lembrar-me</label>
                        </div>

                        <Link to="/recuperar-senha" className="forgot-password">
                            Esqueceu a senha?
                        </Link>
                    </div>

                    <button
                        type="submit"
                        className="login-button"
                        disabled={isSubmitting}
                    >
                        {isSubmitting ? "Entrando..." : "Entrar"}
                    </button>

                    <div className="divider">
                        <span>OU</span>
                    </div>

                    <div className="social-login">
                        <button type="button" className="social-button google">
                            <i className="fab fa-google"></i>
                            Google
                        </button>
                        <button type="button" className="social-button facebook">
                            <i className="fab fa-facebook-f"></i>
                            Facebook
                        </button>
                    </div>
                </form>

                <div className="login-footer">
                    <p>Ainda não tem uma conta?</p>
                    <Link to="/cadastro" className="signup-link">
                        Cadastre-se agora
                    </Link>
                </div>
            </div>
        </div>
    );
};
