const Evento = require('../models/Evento'); // seu model de evento

// backend/controllers/eventosController.js


exports.createEvento = async (req, res) => {
    try {
        const novoEvento = new Evento(req.body);
        const eventoSalvo = await novoEvento.save();
        res.status(201).json(eventoSalvo);
    } catch (error) {
        res.status(400).send(error.message);
    }
};
