import React, { useState, useEffect } from 'react';
import './Eventos.css';
import axios from 'axios';

// Função para buscar eventos do backend
const fetchEventos = async () => {
  // Simular dados para teste
  return [
    { id: 1, titulo: 'Workshop React', descricao: 'Aprenda React do zero' },
    { id: 2, titulo: 'Palestra Node.js', descricao: 'Backend com Node.js' },
    { id: 3, titulo: 'Hackathon', descricao: 'Competição de programação' },
  ];
};

const Eventos = () => {
  const [eventos, setEventos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentEvento, setCurrentEvento] = useState({
    id: null,
    titulo: '',
    descricao: ''
  });
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    const getEventos = async () => {
      try {
        setLoading(true);
        console.log('Carregando eventos...');
        const data = await fetchEventos();
        console.log('Eventos recebidos:', data);
        setEventos(data);
        setError(null);
      } catch (err) {
        console.error('Erro ao buscar eventos:', err);
        setError('Não foi possível carregar os eventos. Por favor, tente novamente.');
      } finally {
        setLoading(false);
      }
    };

    getEventos();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEvento({
      ...currentEvento,
      [name]: value
    });
  };

  const resetForm = () => {
    setCurrentEvento({
      id: null,
      titulo: '',
      descricao: ''
    });
    setEditing(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!currentEvento.titulo.trim()) {
      setError('O título do evento é obrigatório');
      return;
    }
    
    if (editing) {
      // Atualizar evento existente
      const updatedEventos = eventos.map(evento => 
        evento.id === currentEvento.id ? currentEvento : evento
      );
      setEventos(updatedEventos);
    } else {
      // Criar novo evento
      const newEvento = {
        ...currentEvento,
        id: eventos.length > 0 ? Math.max(...eventos.map(e => e.id)) + 1 : 1
      };
      setEventos([...eventos, newEvento]);
    }
    
    resetForm();
  };

  const handleEdit = (evento) => {
    setCurrentEvento(evento);
    setEditing(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Tem certeza que deseja excluir este evento?')) {
      setEventos(eventos.filter(evento => evento.id !== id));
    }
  };

  return (
    <div className="eventos-container">
      <h2 className="eventos-title">Gerenciamento de Eventos</h2>
      
      {error && (
        <div className="eventos-message error">
          <p>{error}</p>
          <button onClick={() => setError(null)}>Fechar</button>
        </div>
      )}

      <form onSubmit={handleSubmit} className="eventos-form">
        <h3>{editing ? 'Editar Evento' : 'Adicionar Novo Evento'}</h3>
        
        <div className="eventos-form-group">
          <label htmlFor="titulo">Título*</label>
          <input 
            type="text" 
            id="titulo" 
            name="titulo" 
            value={currentEvento.titulo} 
            onChange={handleInputChange} 
            required 
          />
        </div>
        
        <div className="eventos-form-group">
          <label htmlFor="descricao">Descrição</label>
          <textarea 
            id="descricao" 
            name="descricao" 
            value={currentEvento.descricao || ''} 
            onChange={handleInputChange} 
            rows="3"
          />
        </div>
        
        <div className="eventos-buttons">
          <button type="submit" className="eventos-button eventos-button-primary">
            {editing ? 'Atualizar' : 'Adicionar'}
          </button>
          <button 
            type="button" 
            onClick={resetForm} 
            className="eventos-button eventos-button-warning"
          >
            Cancelar
          </button>
        </div>
      </form>
      
      {loading ? (
        <p>Carregando eventos...</p>
      ) : (
        <table className="eventos-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Título</th>
              <th>Descrição</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {eventos.length > 0 ? (
              eventos.map((evento) => (
                <tr key={evento.id}>
                  <td>{evento.id}</td>
                  <td>{evento.titulo}</td>
                  <td>{evento.descricao || '-'}</td>
                  <td>
                    <button 
                      onClick={() => handleEdit(evento)} 
                      className="eventos-button eventos-button-small eventos-button-warning"
                    >
                      Editar
                    </button>
                    <button 
                      onClick={() => handleDelete(evento.id)} 
                      className="eventos-button eventos-button-small eventos-button-danger"
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" style={{ textAlign: 'center' }}>Nenhum evento encontrado</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Eventos;
