import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import './styles/cadastro.scss';

export const Cadastro = () => {
    const [form, setForm] = useState({
        nome: "",
        email: "",
        fotoPerfil: "",
        dataNascimento: "",
        senha: "",
        confirmacaoSenha: ""
    });

    const [mensagem, setMensagem] = useState("");
    const [isSubmitting, setIsSubmitting] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (form.senha !== form.confirmacaoSenha) {//verifica senha ._.
            setMensagem("As senhas não conferem!");
            return;
        }

        setIsSubmitting(true);

        try {
            const response = await fetch('http://localhost:5000/api/usuarios/cadastrar', { //
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    nome: form.nome,
                    email: form.email,
                    senha: form.senha,
                    fotoPerfil: form.fotoPerfil,
                    dataNascimento: form.dataNascimento,
                }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                setMensagem(`Erro: ${errorData.erro || "Erro no cadastro"}`);
                setIsSubmitting(false);
                return;
            }

            setMensagem("Cadastro realizado com sucesso!");
            setForm({
                nome: "",
                email: "",
                fotoPerfil: "",
                dataNascimento: "",
                senha: "",
                confirmacaoSenha: "",
            });
            setIsSubmitting(false);
            navigate('/');
        } catch (error) {
            setMensagem("Erro ao conectar com o servidor.");
            setIsSubmitting(false);
        }
    };

    return (
        <div className="cadastro-container">
            {/* Botão de voltar */}
            <button 
                className="back-button" 
                onClick={() => navigate('/')}
                aria-label="Voltar para página inicial"
            >
                <i className="fas fa-arrow-left"></i>
                <span>Voltar</span>
            </button>
            
            <div className="cadastro-card">
                <div className="cadastro-header">
                    <h2>Frash Eventos</h2>
                    <p>Crie sua conta para começar</p>
                </div>
                
                <form onSubmit={handleSubmit} className="cadastro-form">
                    {/* Input de imagem de perfil */}
                    <div className="profile-upload">
                        <label htmlFor="fotoPerfil" className="profile-upload-label">
                            {fotoPerfil ? (
                                <img 
                                    src={URL.createObjectURL(fotoPerfil)} 
                                    alt="Preview" 
                                    className="profile-preview"
                                />
                            ) : (
                                <div className="profile-placeholder">
                                    <i className="fas fa-user"></i>
                                    <span>Adicionar foto</span>
                                </div>
                            )}
                        </label>
                        <input
                            type="file"
                            id="fotoPerfil"
                            name="fotoPerfil"
                            accept="image/*"
                            onChange={(e) => setFotoPerfil(e.target.files[0])}
                            className="profile-input"
                        />
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="nome">Nome completo</label>
                        <input
                            type="text"
                            id="nome"
                            name="nome"
                            value={form.nome}
                            onChange={handleChange}
                            required
                            className="form-control"
                        />
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="email">E-mail</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={form.email}
                            onChange={handleChange}
                            required
                            className="form-control"
                        />
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="dataNascimento">Data de Nascimento</label>
                        <input
                            type="date"
                            id="dataNascimento"
                            name="dataNascimento"
                            value={form.dataNascimento}
                            onChange={handleChange}
                            required
                            className="form-control"
                        />
                    </div>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="senha">Senha</label>
                            <input
                                type="password"
                                id="senha"
                                name="senha"
                                value={form.senha}
                                onChange={handleChange}
                                required
                                className="form-control"
                            />
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="confirmacaoSenha">Confirme a senha</label>
                            <input
                                type="password"
                                id="confirmacaoSenha"
                                name="confirmacaoSenha"
                                value={form.confirmacaoSenha}
                                onChange={handleChange}
                                required
                                className="form-control"
                            />
                        </div>
                    </div>
                    
                    <button type="submit" className="cadastro-button">
                        {isSubmitting ? "Cadastrando..." : "Criar Conta"}
                    </button>
                </form>
                
                <div className="cadastro-footer">
                    <p>Já possui uma conta?</p>
                    <Link to="/login" className="login-link">
                        Fazer login
                    </Link>
                </div>
            </div>
        </div>
    );
};
