import React, { useState, useEffect } from 'react';
import { 
  getEquipes, 
  createEquipe, 
  updateEquipe, 
  deleteEquipe, 
  checkApiConnection,
  checkDbConnection
} from '../../services/equipeService';
import '../Inscricoes/Inscricoes_adaptar.css';

const Equipes = () => {
  const [equipes, setEquipes] = useState([]);
  const [currentEquipe, setCurrentEquipe] = useState({ 
    id: null, 
    nome: '', 
    descricao: '', 
    responsavel: '',
    max_participantes: 10
  });
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [apiStatus, setApiStatus] = useState({ api: null, db: null });
  
  useEffect(() => {
    checkConnections();
    fetchEquipes();
  }, []);

  // Verifica o status das conexões com a API e o banco de dados
  const checkConnections = async () => {
    try {
      const apiOnline = await checkApiConnection();
      const dbOnline = await checkDbConnection();
      
      setApiStatus({ api: apiOnline, db: dbOnline });
      
      if (!apiOnline) {
        setError('O servidor backend não está respondendo. Verifique se ele está rodando.');
      } else if (!dbOnline) {
        setError('O servidor está online, mas há um problema com a conexão ao banco de dados.');
      }
    } catch (error) {
      console.error('Erro ao verificar conexões:', error);
      setError('Não foi possível verificar o status das conexões.');
    }
  };

  useEffect(() => {
    fetchEquipes();
  }, []);

  // Limpar mensagem de sucesso após 3 segundos
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const fetchEquipes = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getEquipes();
      setEquipes(data);
    } catch (error) {
      console.error('Erro ao buscar equipes:', error);
      setError('Não foi possível carregar as equipes. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEquipe({ ...currentEquipe, [name]: value });
  };

  const resetForm = () => {
    setCurrentEquipe({ 
      id: null, 
      nome: '', 
      descricao: '', 
      responsavel: '',
      max_participantes: 10
    });
    setEditing(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      if (editing && currentEquipe.id) {
        await updateEquipe(currentEquipe.id, currentEquipe);
        setSuccessMessage('Equipe atualizada com sucesso!');
      } else {
        // Log para depuração
        console.log('Enviando dados para criar equipe:', currentEquipe);
        
        const result = await createEquipe(currentEquipe);
        console.log('Equipe criada com sucesso:', result);
        
        setSuccessMessage('Equipe criada com sucesso!');
      }
      resetForm();
      fetchEquipes();
    } catch (error) {
      console.error('Erro ao salvar equipe:', error);
      
      // Usar mensagem customizada se disponível
      const errorMessage = error.customMessage || 
                         `Falha ao ${editing ? 'atualizar' : 'criar'} a equipe: ${error.response?.data?.error || error.message}`;
      
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (equipe) => {
    setCurrentEquipe({
      id: equipe.id,
      nome: equipe.nome || '',
      descricao: equipe.descricao || '',
      responsavel: equipe.responsavel || '',
      max_participantes: equipe.max_participantes || 10
    });
    setEditing(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta equipe?')) {
      setLoading(true);
      setError(null);
      try {
        await deleteEquipe(id);
        setSuccessMessage('Equipe excluída com sucesso!');
        fetchEquipes();
      } catch (error) {
        console.error('Erro ao excluir equipe:', error);
        const errorMsg = error.response?.data?.error || 'Erro ao excluir equipe';
        setError(errorMsg);
        setLoading(false);
      }
    }
  };

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2 className="equipes-title">Gerenciamento de Equipes</h2>
      </div>

      {/* Status da conexão */}
      {apiStatus.api === false && (
        <div className="equipes-message error">
          <p><strong>Erro de Conexão:</strong> Não foi possível conectar ao servidor backend. Verifique se ele está rodando.</p>
          <button onClick={checkConnections} className="equipes-button equipes-button-primary">
            Verificar Novamente
          </button>
        </div>
      )}
      
      {apiStatus.api === true && apiStatus.db === false && (
        <div className="equipes-message error">
          <p><strong>Erro de Banco de Dados:</strong> O servidor está online, mas há um problema com a conexão ao banco de dados.</p>
          <button onClick={checkConnections} className="equipes-button equipes-button-primary">
            Verificar Novamente
          </button>
        </div>
      )}

      {/* Mensagem de erro */}
      {error && (
        <div className="equipes-message error">
          <p>{error}</p>
          <button onClick={() => setError(null)} className="equipes-button equipes-button-danger">
            Fechar
          </button>
        </div>
      )}

      {/* Mensagem de sucesso */}
      {successMessage && (
        <div className="equipes-message success">
          <p>{successMessage}</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="equipes-form">
        <h3>{editing ? 'Editar Equipe' : 'Adicionar Nova Equipe'}</h3>
        
        <div className="equipes-form-group">
          <label htmlFor="nome">Nome da Equipe*</label>
          <input 
            type="text" 
            id="nome" 
            name="nome" 
            value={currentEquipe.nome} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="descricao">Descrição</label>
          <input 
            type="text" 
            id="descricao" 
            name="descricao" 
            value={currentEquipe.descricao || ''} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="responsavel">Responsável</label>
          <input 
            type="text" 
            id="responsavel" 
            name="responsavel" 
            value={currentEquipe.responsavel || ''} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="max_participantes">Máximo de Participantes</label>
          <input 
            type="number" 
            id="max_participantes" 
            name="max_participantes" 
            min="1"
            value={currentEquipe.max_participantes || 10} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-buttons">
          <button 
            type="submit" 
            className="equipes-button equipes-button-primary"
            disabled={loading}
          >
            {loading ? 'Processando...' : (editing ? 'Atualizar' : 'Adicionar')}
          </button>
          <button 
            type="button" 
            onClick={resetForm} 
            className="equipes-button equipes-button-warning"
            disabled={loading}
          >
            Cancelar
          </button>
        </div>
      </form>

      {loading && !equipes.length ? (
        <p>Carregando equipes...</p>
      ) : (
        <table className="equipes-table">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Responsável</th>
              <th>Vagas</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {equipes.length > 0 ? (
              equipes.map((equipe) => (
                <tr key={equipe.id}>
                  <td>{equipe.nome}</td>
                  <td>{equipe.descricao || '-'}</td>
                  <td>{equipe.responsavel || '-'}</td>
                  <td>{equipe.max_participantes || 10}</td>
                  <td className="equipes-buttons">
                    <button 
                      onClick={() => handleEdit(equipe)} 
                      className="equipes-button equipes-button-warning"
                      disabled={loading}
                    >
                      Editar
                    </button>
                    <button 
                      onClick={() => handleDelete(equipe.id)} 
                      className="equipes-button equipes-button-danger"
                      disabled={loading}
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" style={{ textAlign: 'center' }}>Nenhuma equipe encontrada</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Equipes;
