import React from 'react';
import './ApiErrorFallback.scss';

const ApiErrorFallback = ({ message, onRetry }) => {
  return (
    <div className="api-error-fallback">
      <div className="error-icon">
        <i className="fas fa-exclamation-triangle"></i>
      </div>
      <h3>Erro de Conexão</h3>
      <p>{message || "Não foi possível conectar ao servidor. Verifique sua conexão e tente novamente."}</p>
      {onRetry && (
        <button className="retry-button" onClick={onRetry}>
          Tentar Novamente
        </button>
      )}
      <p className="note">Enquanto isso, estamos mostrando dados de exemplo para você.</p>
    </div>
  );
};

export default ApiErrorFallback;
