import React, { useState, useEffect } from 'react';
import { getEquipes, createEquipe, updateEquipe, deleteEquipe, testConnection } from '../../services/equipeService';
import '../Inscricoes/Inscricoes_adaptar.css';

const Equipes = () => {
  const [equipes, setEquipes] = useState([]);
  const [currentEquipe, setCurrentEquipe] = useState({ id: null, nome: '', descricao: '', responsavel: '' });
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [connectionOk, setConnectionOk] = useState(null);

  // Testar conexão ao montar o componente
  useEffect(() => {
    const checkConnection = async () => {
      const isConnected = await testConnection();
      setConnectionOk(isConnected);
      if (isConnected) {
        fetchEquipes();
      }
    };
    
    checkConnection();
  }, []);

  const fetchEquipes = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getEquipes();
      setEquipes(data);
      setLoading(false);
    } catch (error) {
      console.error('Erro ao buscar equipes:', error);
      setError('Não foi possível carregar as equipes. Por favor, tente novamente.');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEquipe({ ...currentEquipe, [name]: value });
  };

  const resetForm = () => {
    setCurrentEquipe({ id: null, nome: '', descricao: '', responsavel: '' });
    setEditing(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      if (editing) {
        await updateEquipe(currentEquipe.id, currentEquipe);
        alert('Equipe atualizada com sucesso!');
      } else {
        await createEquipe(currentEquipe);
        alert('Equipe criada com sucesso!');
      }
      resetForm();
      fetchEquipes();
    } catch (error) {
      console.error('Erro ao salvar equipe:', error);
      setError('Falha ao salvar a equipe. Verifique os dados e tente novamente.');
      setLoading(false);
    }
  };

  const handleEdit = (equipe) => {
    setCurrentEquipe({ ...equipe });
    setEditing(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta equipe?')) {
      setLoading(true);
      try {
        await deleteEquipe(id);
        alert('Equipe excluída com sucesso!');
        fetchEquipes();
      } catch (error) {
        console.error('Erro ao excluir equipe:', error);
        setError('Falha ao excluir a equipe. Tente novamente.');
        setLoading(false);
      }
    }
  };

  // Renderização condicional baseada no estado da conexão e carregamento
  if (connectionOk === false) {
    return (
      <div className="equipes-container">
        <div className="equipes-error">
          <h2>Erro de Conexão</h2>
          <p>Não foi possível conectar ao servidor. Verifique se o backend está rodando e tente novamente.</p>
          <button 
            onClick={() => window.location.reload()} 
            className="equipes-button equipes-button-primary"
          >
            Tentar Novamente
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2 className="equipes-title">Gerenciamento de Equipes</h2>
      </div>

      {error && (
        <div className="equipes-error" style={{backgroundColor: '#ffdddd', padding: '10px', marginBottom: '20px', borderRadius: '5px'}}>
          <p>{error}</p>
          <button onClick={() => setError(null)} className="equipes-button equipes-button-danger" style={{marginTop: '10px'}}>
            Fechar
          </button>
        </div>
      )}

      <form onSubmit={handleSubmit} className="equipes-form">
        <h3>{editing ? 'Editar Equipe' : 'Adicionar Nova Equipe'}</h3>
        
        <div className="equipes-form-group">
          <label htmlFor="nome">Nome da Equipe*</label>
          <input 
            type="text" 
            id="nome" 
            name="nome" 
            value={currentEquipe.nome} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="descricao">Descrição</label>
          <input 
            type="text" 
            id="descricao" 
            name="descricao" 
            value={currentEquipe.descricao || ''} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="responsavel">Responsável</label>
          <input 
            type="text" 
            id="responsavel" 
            name="responsavel" 
            value={currentEquipe.responsavel || ''} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-buttons">
          <button 
            type="submit" 
            className="equipes-button equipes-button-primary"
            disabled={loading}
          >
            {loading ? 'Processando...' : (editing ? 'Atualizar' : 'Adicionar')}
          </button>
          <button 
            type="button" 
            onClick={resetForm} 
            className="equipes-button equipes-button-warning"
            disabled={loading}
          >
            Cancelar
          </button>
        </div>
      </form>

      {loading ? (
        <p>Carregando...</p>
      ) : (
        <table className="equipes-table">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Responsável</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {equipes.length > 0 ? (
              equipes.map((equipe) => (
                <tr key={equipe.id}>
                  <td>{equipe.nome}</td>
                  <td>{equipe.descricao}</td>
                  <td>{equipe.responsavel}</td>
                  <td className="equipes-buttons">
                    <button 
                      onClick={() => handleEdit(equipe)} 
                      className="equipes-button equipes-button-warning"
                    >
                      Editar
                    </button>
                    <button 
                      onClick={() => handleDelete(equipe.id)} 
                      className="equipes-button equipes-button-danger"
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" style={{ textAlign: 'center' }}>Nenhuma equipe encontrada</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Equipes;
