import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './styles/login.scss';

export const Login = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        email: '',
        senha: '',
        lembrarMe: false
    });
    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [loginError, setLoginError] = useState(null);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value
        });
        
        // Limpar erro ao digitar
        if (errors[name]) {
            setErrors({
                ...errors,
                [name]: null
            });
        }
    };

    const validateForm = () => {
        const newErrors = {};
        
        if (!formData.email) {
            newErrors.email = 'E-mail é obrigatório';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'E-mail inválido';
        }
        
        if (!formData.senha) {
            newErrors.senha = 'Senha é obrigatória';
        }
        
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) {
            // Adicionar animação de shake no card de login
            const loginCard = document.querySelector('.login-card');
            loginCard.classList.add('shake');
            setTimeout(() => {
                loginCard.classList.remove('shake');
            }, 600);
            return;
        }
        
        setIsSubmitting(true);
        setLoginError(null);
        
        try {
            // Simulação de login (sem verificar credenciais específicas)
            await new Promise(resolve => setTimeout(resolve, 1000));
            
            // Login sempre bem-sucedido
            if (formData.lembrarMe) {
                localStorage.setItem('lembrarMe', 'true');
            }
            localStorage.setItem('usuarioLogado', JSON.stringify({ 
                email: formData.email,
                nome: 'Usuário'
            }));
            navigate('/menu'); // Alterado de '/dashboard' para '/menu'
        } catch (error) {
            console.error('Erro ao fazer login:', error);
            setLoginError('Ocorreu um erro ao tentar fazer login. Por favor, tente novamente.');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="login-container">
            {/* Botão de voltar */}
            <button 
                className="back-button" 
                onClick={() => navigate('/')}
                aria-label="Voltar para página inicial"
            >
                <i className="fas fa-arrow-left"></i>
            </button>
            
            <div className="login-card">
                <div className="login-header">
                    <h2>Bem-vindo</h2>
                    <p>Faça login para acessar sua conta</p>
                </div>
                
                <form className="login-form" onSubmit={handleSubmit}>
                    {loginError && (
                        <div className="error-message">
                            {loginError}
                            <button 
                                type="button" 
                                className="close-button"
                                onClick={() => setLoginError(null)}
                            >
                                &times;
                            </button>
                        </div>
                    )}
                    
                    <div className="form-group">
                        <label htmlFor="email">E-mail</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            className={errors.email ? 'error' : ''}
                            disabled={isSubmitting}
                            placeholder="seu@email.com"
                        />
                        {errors.email && (
                            <div className="input-error">{errors.email}</div>
                        )}
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="senha">Senha</label>
                        <input
                            type="password"
                            id="senha"
                            name="senha"
                            value={formData.senha}
                            onChange={handleChange}
                            className={errors.senha ? 'error' : ''}
                            disabled={isSubmitting}
                            placeholder="Sua senha"
                        />
                        {errors.senha && (
                            <div className="input-error">{errors.senha}</div>
                        )}
                    </div>
                    
                    <div className="login-options">
                        <div className="remember-me">
                            <input
                                type="checkbox"
                                id="lembrarMe"
                                name="lembrarMe"
                                checked={formData.lembrarMe}
                                onChange={handleChange}
                                disabled={isSubmitting}
                            />
                            <label htmlFor="lembrarMe">Lembrar-me</label>
                        </div>
                        
                        <Link to="/recuperar-senha" className="forgot-password">
                            Esqueci minha senha
                        </Link>
                    </div>
                    
                    <button
                        type="submit"
                        className="login-button"
                        disabled={isSubmitting}
                    >
                        {isSubmitting ? 'Entrando...' : 'Entrar'}
                    </button>
                </form>
                
                <div className="login-footer">
                    <p>Ainda não tem uma conta?</p>
                    <Link to="/cadastro" className="register-link">
                        Cadastre-se agora
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default Login;
