import React from 'react';
import { Link } from 'react-router-dom';
import './styles/menu.scss';

export const Menu = () => {
  // Recuperar informações do usuário do localStorage
  const usuarioLogado = JSON.parse(localStorage.getItem('usuarioLogado') || '{}');
  
  return (
    <div className="menu-container">
      <div className="menu-header">
        <h1>Menu Principal</h1>
        <p>Bem-vindo, {usuarioLogado.nome || 'Usuário'}!</p>
      </div>
      
      <div className="menu-options">
        <div className="menu-card">
          <i className="fas fa-users"></i>
          <h2>Gerenciar Equipes</h2>
          <p>Adicione, edite e gerencie equipes</p>
          <Link to="/equipes" className="menu-button">Acessar</Link>
        </div>
        
        <div className="menu-card">
          <i className="fas fa-tasks"></i>
          <h2>Tarefas</h2>
          <p>Acompanhe suas tarefas e projetos</p>
          <Link to="/tarefas" className="menu-button">Acessar</Link>
        </div>
        
        <div className="menu-card">
          <i className="fas fa-cog"></i>
          <h2>Configurações</h2>
          <p>Personalize suas preferências</p>
          <Link to="/configuracoes" className="menu-button">Acessar</Link>
        </div>
      </div>
      
      <div className="menu-footer">
        <button 
          className="logout-button"
          onClick={() => {
            localStorage.removeItem('usuarioLogado');
            localStorage.removeItem('lembrarMe');
            window.location.href = '/';
          }}
        >
          <i className="fas fa-sign-out-alt"></i> Sair
        </button>
      </div>
    </div>
  );
};

export default Menu;
