import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import './styles/cadastro.scss';

export const Cadastro = () => {
    const navigate = useNavigate();
    const fileInputRef = useRef(null);
    const [formData, setFormData] = useState({
        nome: "",
        email: "",
        fotoPerfil: "",
        dataNascimento: "",
        senha: "",
        confirmacaoSenha: "",
    });
    const [previewUrl, setPreviewUrl] = useState('');
    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [cadastroError, setCadastroError] = useState(null);
    const [cadastroSuccess, setCadastroSuccess] = useState(null);
    const [passwordStrength, setPasswordStrength] = useState('');
    const [passwordCriteria, setPasswordCriteria] = useState({
        length: false,
        lowercase: false,
        uppercase: false,
        number: false,
        special: false
    });

    useEffect(() => {
        if (formData.senha) {
            checkPasswordStrength(formData.senha);
        } else {
            setPasswordStrength('');
            setPasswordCriteria({
                length: false,
                lowercase: false,
                uppercase: false,
                number: false,
                special: false
            });
        }
    }, [formData.senha]);

    const checkPasswordStrength = (password) => {
        // Verificar os critérios de senha
        const criteria = {
            length: password.length >= 8,
            lowercase: /[a-z]/.test(password),
            uppercase: /[A-Z]/.test(password),
            number: /[0-9]/.test(password),
            special: /[^A-Za-z0-9]/.test(password)
        };

        setPasswordCriteria(criteria);

        // Calcular força da senha
        const criteriaCount = Object.values(criteria).filter(Boolean).length;
        
        if (criteriaCount <= 2) {
            setPasswordStrength('weak');
        } else if (criteriaCount <= 4) {
            setPasswordStrength('medium');
        } else {
            setPasswordStrength('strong');
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });

        // Limpar erros para este campo quando o usuário digita
        if (errors[name]) {
            setErrors({
                ...errors,
                [name]: null
            });
        }
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Guardar o arquivo no estado
            setFormData({
                ...formData,
                fotoPerfil: file
            });
            
            // Criar URL para preview
            const reader = new FileReader();
            reader.onload = () => {
                setPreviewUrl(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleImageClick = () => {
        fileInputRef.current.click();
    };

    const validateForm = () => {
        const newErrors = {};

        // Validar nome
        if (!formData.nome) {
            newErrors.nome = "Nome é obrigatório";
        }

        // Validar email
        if (!formData.email) {
            newErrors.email = "E-mail é obrigatório";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = "E-mail inválido";
        }

        // Validar data de nascimento - alterado para 10 anos
        if (!formData.dataNascimento) {
            newErrors.dataNascimento = "Data de nascimento é obrigatória";
        } else {
            // Verificar idade mínima (10 anos)
            const birthDate = new Date(formData.dataNascimento);
            const today = new Date();
            const age = today.getFullYear() - birthDate.getFullYear();
            const monthDiff = today.getMonth() - birthDate.getMonth();
            
            if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
                if (age - 1 < 10) {
                    newErrors.dataNascimento = "Você deve ter pelo menos 10 anos";
                }
            } else if (age < 10) {
                newErrors.dataNascimento = "Você deve ter pelo menos 10 anos";
            }
        }

        // Validar senha
        if (!formData.senha) {
            newErrors.senha = "Senha é obrigatória";
        } else if (formData.senha.length < 8) {
            newErrors.senha = "Senha deve ter pelo menos 8 caracteres";
        } else if (passwordStrength === 'weak') {
            newErrors.senha = "Sua senha é fraca. Tente melhorá-la.";
        }

        // Validar confirmação de senha
        if (!formData.confirmacaoSenha) {
            newErrors.confirmacaoSenha = "Confirmação de senha é obrigatória";
        } else if (formData.senha !== formData.confirmacaoSenha) {
            newErrors.confirmacaoSenha = "As senhas não coincidem";
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return;
        }

        setIsSubmitting(true);
        setCadastroError(null);
        setCadastroSuccess(null);

        try {
            // Criar FormData para enviar a imagem
            const formDataWithImage = new FormData();
            formDataWithImage.append('nome', formData.nome);
            formDataWithImage.append('email', formData.email);
            formDataWithImage.append('dataNascimento', formData.dataNascimento);
            formDataWithImage.append('senha', formData.senha);
            if (formData.fotoPerfil) {
                formDataWithImage.append('fotoPerfil', formData.fotoPerfil);
            }

            // Simular API call
            await new Promise(resolve => setTimeout(resolve, 1500));

            // Mock cadastro validation - Replace with actual API call
            if (formData.email !== "usuario@exemplo.com") {
                // Success
                setCadastroSuccess("Cadastro realizado com sucesso! Redirecionando para o login...");
                
                // Limpar formulário
                setFormData({
                    nome: "",
                    email: "",
                    fotoPerfil: "",
                    dataNascimento: "",
                    senha: "",
                    confirmacaoSenha: "",
                });
                setPreviewUrl('');
                
                // Redirecionar após 2 segundos
                setTimeout(() => {
                    navigate("/login");
                }, 2000);
            } else {
                setCadastroError("Este e-mail já está cadastrado. Por favor, use outro e-mail ou faça login.");
            }
        } catch (error) {
            console.error("Cadastro error:", error);
            setCadastroError("Ocorreu um erro ao fazer o cadastro. Por favor, tente novamente.");
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="cadastro-page">
            <div className="cadastro-container">
                <div className="cadastro-header">
                    <h1>Crie sua conta</h1>
                    <p>Preencha os campos abaixo para se cadastrar</p>
                </div>

                <form className="cadastro-form" onSubmit={handleSubmit}>
                    {cadastroError && (
                        <div className="error-alert">
                            {cadastroError}
                            <button 
                                type="button" 
                                className="close-button"
                                onClick={() => setCadastroError(null)}
                            >
                                &times;
                            </button>
                        </div>
                    )}

                    {cadastroSuccess && (
                        <div className="success-alert">
                            <p>{cadastroSuccess}</p>
                        </div>
                    )}

                    <div className="profile-image-upload">
                        <div 
                            className="profile-image-container" 
                            onClick={handleImageClick}
                        >
                            {previewUrl ? (
                                <img 
                                    src={previewUrl} 
                                    alt="Imagem de perfil" 
                                    className="profile-preview" 
                                />
                            ) : (
                                <div className="profile-placeholder">
                                    <i className="fas fa-user"></i>
                                    <span>Adicionar foto</span>
                                </div>
                            )}
                        </div>
                        <input
                            type="file"
                            ref={fileInputRef}
                            onChange={handleImageChange}
                            accept="image/*"
                            className="file-input"
                            disabled={isSubmitting}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="nome">Nome completo</label>
                        <input
                            type="text"
                            id="nome"
                            name="nome"
                            value={formData.nome}
                            onChange={handleChange}
                            placeholder="Seu nome completo"
                            className={errors.nome ? "error" : ""}
                            disabled={isSubmitting}
                        />
                        {errors.nome && (
                            <div className="error-message">{errors.nome}</div>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">E-mail</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            placeholder="seu@email.com"
                            className={errors.email ? "error" : ""}
                            disabled={isSubmitting}
                        />
                        {errors.email && (
                            <div className="error-message">{errors.email}</div>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="dataNascimento">Data de Nascimento</label>
                        <input
                            type="date"
                            id="dataNascimento"
                            name="dataNascimento"
                            value={formData.dataNascimento}
                            onChange={handleChange}
                            className={errors.dataNascimento ? "error" : ""}
                            disabled={isSubmitting}
                        />
                        {errors.dataNascimento && (
                            <div className="error-message">{errors.dataNascimento}</div>
                        )}
                    </div>

                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="senha">Senha</label>
                            <input
                                type="password"
                                id="senha"
                                name="senha"
                                value={formData.senha}
                                onChange={handleChange}
                                placeholder="Crie uma senha forte"
                                className={errors.senha ? "error" : ""}
                                disabled={isSubmitting}
                            />
                            {formData.senha && (
                                <div className="password-strength">
                                    <div className={`progress ${passwordStrength}`}></div>
                                </div>
                            )}
                            {formData.senha && (
                                <div className="password-tips">
                                    Sua senha deve conter:
                                    <ul>
                                        <li className={passwordCriteria.length ? "valid" : ""}>
                                            Pelo menos 8 caracteres
                                        </li>
                                        <li className={passwordCriteria.lowercase ? "valid" : ""}>
                                            Letras minúsculas (a-z)
                                        </li>
                                        <li className={passwordCriteria.uppercase ? "valid" : ""}>
                                            Letras maiúsculas (A-Z)
                                        </li>
                                        <li className={passwordCriteria.number ? "valid" : ""}>
                                            Números (0-9)
                                        </li>
                                        <li className={passwordCriteria.special ? "valid" : ""}>
                                            Caracteres especiais (!@#$%^&*)
                                        </li>
                                    </ul>
                                </div>
                            )}
                            {errors.senha && (
                                <div className="error-message">{errors.senha}</div>
                            )}
                        </div>

                        <div className="form-group">
                            <label htmlFor="confirmacaoSenha">Confirmar senha</label>
                            <input
                                type="password"
                                id="confirmacaoSenha"
                                name="confirmacaoSenha"
                                value={formData.confirmacaoSenha}
                                onChange={handleChange}
                                placeholder="Digite a senha novamente"
                                className={errors.confirmacaoSenha ? "error" : ""}
                                disabled={isSubmitting}
                            />
                            {errors.confirmacaoSenha && (
                                <div className="error-message">{errors.confirmacaoSenha}</div>
                            )}
                        </div>
                    </div>

                    <button
                        type="submit"
                        className="cadastro-button"
                        disabled={isSubmitting}
                    >
                        {isSubmitting ? "Cadastrando..." : "Criar Conta"}
                    </button>
                </form>

                <div className="cadastro-footer">
                    <p>Já tem uma conta?</p>
                    <Link to="/login" className="login-link">
                        Faça login agora
                    </Link>
                </div>
            </div>
        </div>
    );
};
