import React, { useState, useEffect } from "react";
import EventoModalView from "../Modals/EventoModalView";
import { EventoModalAction } from "../Modals/EventoModalAction";

const Eventos = () => {
  const [modalActionOpen, setModalActionOpen] = useState(false);
  const [modalViewOpen, setModalViewOpen] = useState(false);
  const [eventoSelecionado, setEventoSelecionado] = useState(null);
  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    // Aqui você faz a chamada real para sua API ou banco
    // Exemplo simulando com setTimeout:
    const carregarEventos = async () => {
      // Exemplo: fetch('/api/eventos').then...
      const dadosFalsos = [
        {
          id: 1,
          titulo: "Reunião Geral",
          data: "2025-06-07T10:00:00",
          local: "Sala 1",
          status: "Confirmado",
          descricao: "Discussão dos projetos do semestre",
        },
        {
          id: 2,
          titulo: "Apresentação Projeto X",
          data: "2025-06-10T14:00:00",
          local: "Auditório",
          status: "Pendente",
          descricao: "Apresentação do novo projeto da equipe",
        },
      ];
      // Simula atraso da requisição
      setTimeout(() => {
        setEventos(dadosFalsos);
      }, 1000);
    };

    carregarEventos();
  }, []);

  const abrirModalAction = (evento) => {
    setEventoSelecionado(evento);
    setModalActionOpen(true);
  };

  const abrirModalView = () => {
    setModalActionOpen(false);
    setModalViewOpen(true);
  };

  return (
    <div>
      <h1>Lista de Eventos</h1>

      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Título</th>
            <th>Data</th>
            <th>Local</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {eventos.length === 0 ? (
            <tr>
              <td colSpan="5" style={{ textAlign: "center" }}>Carregando eventos...</td>
            </tr>
          ) : (
            eventos.map((evento) => (
              <tr key={evento.id}>
                <td>{evento.titulo}</td>
                <td>{new Date(evento.data).toLocaleDateString()}</td>
                <td>{evento.local}</td>
                <td>{evento.status}</td>
                <td>
                  <button onClick={() => abrirModalAction(evento)}>Ações</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      <EventoModalAction
        open={modalActionOpen}
        onClose={() => setModalActionOpen(false)}
        onView={abrirModalView}
        onEdit={() => alert("Editar clicado")}
        onDelete={() => alert("Deletar clicado")}
      />

      <EventoModalView
        isOpen={modalViewOpen}
        onClose={() => setModalViewOpen(false)}
        evento={eventoSelecionado}
      />
    </div>
  );
};

export default Eventos;
