import axios from 'axios';

const API_URL = 'http://localhost:3001/equipes';

// Interceptors para logs detalhados
axios.interceptors.request.use(
  config => {
    console.log(`[API Request] ${config.method.toUpperCase()} ${config.url}`, config.data || '');
    return config;
  },
  error => {
    console.error('[API Request Error]', error);
    return Promise.reject(error);
  }
);

axios.interceptors.response.use(
  response => {
    console.log(`[API Response] Status: ${response.status}`, response.data);
    return response;
  },
  error => {
    console.error('[API Response Error]', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

export const getEquipes = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error('Erro ao buscar equipes:', error);
    throw error;
  }
};

export const getEquipe = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Erro ao buscar equipe ${id}:`, error);
    throw error;
  }
};

export const createEquipe = async (equipe) => {
  try {
    const response = await axios.post(API_URL, equipe);
    return response.data;
  } catch (error) {
    console.error('Erro ao criar equipe:', error);
    throw error;
  }
};

export const updateEquipe = async (id, equipe) => {
  try {
    // Garantir que o id seja um número
    const numericId = parseInt(id);
    
    // Log mais detalhado
    console.log(`Enviando solicitação de atualização para equipe ID: ${numericId}`, equipe);
    
    const response = await axios.put(`${API_URL}/${numericId}`, equipe);
    
    console.log(`Resposta da atualização da equipe ID: ${numericId}:`, response.data);
    return response.data;
  } catch (error) {
    console.error(`Erro ao atualizar equipe ${id}:`, error);
    throw error;
  }
};

export const deleteEquipe = async (id) => {
  try {
    // Garantir que o id seja um número
    const numericId = parseInt(id);
    
    // Log mais detalhado
    console.log(`Enviando solicitação de exclusão para equipe ID: ${numericId}`);
    
    const response = await axios.delete(`${API_URL}/${numericId}`);
    
    console.log(`Resposta da exclusão da equipe ID: ${numericId}:`, response.data);
    return response.data;
  } catch (error) {
    console.error(`Erro ao excluir equipe ${id}:`, error);
    throw error;
  }
};
