import React, { useState, useEffect } from 'react';
import { getEquipes, verificarVagasEquipe } from '../../services/equipeService';
import { getInscricoesUsuario, inscreverUsuario, cancelarInscricao } from '../../services/inscricaoEquipeService';
import './Inscricoes_adaptar.css';

const InscricoesEquipe = () => {
  const [equipes, setEquipes] = useState([]);
  const [minhasInscricoes, setMinhasInscricoes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [selectedEquipe, setSelectedEquipe] = useState('');
  
  // ID do usuário logado (em uma aplicação real, viria do contexto de autenticação)
  const usuarioId = localStorage.getItem('userId') || 1;

  useEffect(() => {
    fetchEquipes();
    fetchMinhasInscricoes();
  }, []);

  // Limpar mensagem de sucesso após 3 segundos
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const fetchEquipes = async () => {
    try {
      setLoading(true);
      const data = await getEquipes();
      
      // Para cada equipe, verificar vagas disponíveis
      const equipesComVagas = await Promise.all(
        data.map(async (equipe) => {
          try {
            const vagasInfo = await verificarVagasEquipe(equipe.id);
            return {
              ...equipe,
              total_inscritos: vagasInfo.total_inscritos || 0,
              vagas_disponiveis: vagasInfo.vagas_disponiveis || equipe.max_participantes,
              disponivel: vagasInfo.disponivel !== false
            };
          } catch (error) {
            console.error(`Erro ao verificar vagas da equipe ${equipe.id}:`, error);
            return {
              ...equipe,
              total_inscritos: 0,
              vagas_disponiveis: equipe.max_participantes,
              disponivel: true
            };
          }
        })
      );
      
      setEquipes(equipesComVagas);
    } catch (error) {
      console.error('Erro ao buscar equipes:', error);
      setError('Não foi possível carregar as equipes. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const fetchMinhasInscricoes = async () => {
    try {
      setLoading(true);
      const data = await getInscricoesUsuario(usuarioId);
      setMinhasInscricoes(data);
    } catch (error) {
      console.error('Erro ao buscar minhas inscrições:', error);
      setError('Não foi possível carregar suas inscrições. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleInscrever = async () => {
    if (!selectedEquipe) {
      setError('Selecione uma equipe para se inscrever');
      return;
    }

    setLoading(true);
    setError(null);
    
    try {
      await inscreverUsuario(usuarioId, selectedEquipe);
      setSuccessMessage('Inscrição realizada com sucesso!');
      setSelectedEquipe('');
      
      // Atualizar listas
      fetchEquipes();
      fetchMinhasInscricoes();
    } catch (error) {
      console.error('Erro ao realizar inscrição:', error);
      setError(error.response?.data?.error || 'Erro ao realizar inscrição');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelarInscricao = async (inscricaoId) => {
    if (window.confirm('Tem certeza que deseja cancelar esta inscrição?')) {
      setLoading(true);
      setError(null);
      
      try {
        await cancelarInscricao(inscricaoId);
        setSuccessMessage('Inscrição cancelada com sucesso!');
        
        // Atualizar listas
        fetchEquipes();
        fetchMinhasInscricoes();
      } catch (error) {
        console.error('Erro ao cancelar inscrição:', error);
        setError(error.response?.data?.error || 'Erro ao cancelar inscrição');
      } finally {
        setLoading(false);
      }
    }
  };

  // Verificar se o usuário já está inscrito em uma equipe
  const jaInscrito = (equipeId) => {
    return minhasInscricoes.some(inscricao => inscricao.equipe_id === equipeId);
  };

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2 className="equipes-title">Inscrições em Equipes</h2>
      </div>

      {/* Mensagem de erro */}
      {error && (
        <div className="equipes-message error">
          <p>{error}</p>
          <button onClick={() => setError(null)} className="equipes-button equipes-button-danger">
            Fechar
          </button>
        </div>
      )}

      {/* Mensagem de sucesso */}
      {successMessage && (
        <div className="equipes-message success">
          <p>{successMessage}</p>
        </div>
      )}

      {/* Formulário de inscrição */}
      <div className="equipes-form">
        <h3>Inscrever-se em uma Equipe</h3>
        
        <div className="equipes-form-group">
          <label htmlFor="equipe">Selecione uma Equipe</label>
          <select 
            id="equipe" 
            name="equipe"
            value={selectedEquipe}
            onChange={(e) => setSelectedEquipe(e.target.value)}
            disabled={loading}
          >
            <option value="">Selecione...</option>
            {equipes.map(equipe => (
              <option 
                key={equipe.id} 
                value={equipe.id}
                disabled={!equipe.disponivel || jaInscrito(equipe.id)}
              >
                {equipe.nome} 
                {!equipe.disponivel ? ' (Sem vagas)' : ''} 
                {jaInscrito(equipe.id) ? ' (Já inscrito)' : ''}
              </option>
            ))}
          </select>
        </div>

        <div className="equipes-buttons">
          <button 
            onClick={handleInscrever} 
            className="equipes-button equipes-button-primary"
            disabled={loading || !selectedEquipe}
          >
            {loading ? 'Processando...' : 'Inscrever-se'}
          </button>
        </div>
      </div>

      {/* Minhas inscrições */}
      <h3>Minhas Inscrições</h3>
      {loading && !minhasInscricoes.length ? (
        <p>Carregando inscrições...</p>
      ) : (
        <table className="equipes-table">
          <thead>
            <tr>
              <th>Equipe</th>
              <th>Descrição</th>
              <th>Responsável</th>
              <th>Data de Inscrição</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {minhasInscricoes.length > 0 ? (
              minhasInscricoes.map((inscricao) => (
                <tr key={inscricao.id}>
                  <td>{inscricao.nome_equipe}</td>
                  <td>{inscricao.descricao_equipe || '-'}</td>
                  <td>{inscricao.responsavel || '-'}</td>
                  <td>{new Date(inscricao.data_inscricao).toLocaleDateString()}</td>
                  <td>
                    <button 
                      onClick={() => handleCancelarInscricao(inscricao.id)} 
                      className="equipes-button equipes-button-danger"
                      disabled={loading}
                    >
                      Cancelar
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" style={{ textAlign: 'center' }}>Você não está inscrito em nenhuma equipe</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
      
      {/* Lista de todas as equipes disponíveis */}
      <h3>Equipes Disponíveis</h3>
      {loading && !equipes.length ? (
        <p>Carregando equipes...</p>
      ) : (
        <table className="equipes-table">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Responsável</th>
              <th>Vagas</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {equipes.length > 0 ? (
              equipes.map((equipe) => (
                <tr key={equipe.id}>
                  <td>{equipe.nome}</td>
                  <td>{equipe.descricao || '-'}</td>
                  <td>{equipe.responsavel || '-'}</td>
                  <td>
                    {equipe.total_inscritos || 0} / {equipe.max_participantes || 10}
                  </td>
                  <td>
                    {jaInscrito(equipe.id) ? (
                      <span style={{ color: 'green' }}>Inscrito</span>
                    ) : equipe.disponivel ? (
                      <span style={{ color: 'blue' }}>Disponível</span>
                    ) : (
                      <span style={{ color: 'red' }}>Sem vagas</span>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" style={{ textAlign: 'center' }}>Nenhuma equipe disponível</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default InscricoesEquipe;
