import React, { useState, useEffect } from "react";
import EventoModalView from "../Modals/EventoModalView";
import { EventoModalAction } from "../Modals/EventoModalAction";

const Eventos = () => {
  const [eventos, setEventos] = useState([]);
  const [modalActionOpen, setModalActionOpen] = useState(false);
  const [modalViewOpen, setModalViewOpen] = useState(false);
  const [eventoSelecionado, setEventoSelecionado] = useState(null);

  // Busca os eventos do backend
  useEffect(() => {
    fetch("http://localhost:5000/api/eventos") // ou substitua pelo domínio da sua API
      .then((res) => res.json())
      .then((data) => setEventos(data))
      .catch((err) => console.error("Erro ao buscar eventos:", err));
  }, []);

  const abrirModalAction = (evento) => {
    setEventoSelecionado(evento);
    setModalActionOpen(true);
  };

  const abrirModalView = () => {
    setModalActionOpen(false);
    setModalViewOpen(true);
  };

  return (
    <div>
      <h1>Lista de Eventos</h1>

      <table
        border="1"
        cellPadding="8"
        style={{ width: "100%", borderCollapse: "collapse" }}
      >
        <thead>
          <tr>
            <th>Título</th>
            <th>Data</th>
            <th>Equipe</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {eventos.map((evento) => (
            <tr key={evento.id}>
              <td>{evento.nome}</td>
              <td>{new Date(evento.data).toLocaleDateString()}</td>
              <td>{evento.equipe}</td>
              <td>{evento.status}</td>
              <td>
                <button onClick={() => abrirModalAction(evento)}>Ações</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <EventoModalAction
        open={modalActionOpen}
        onClose={() => setModalActionOpen(false)}
        onView={abrirModalView}
        onEdit={() => alert("Editar clicado")}
        onDelete={() => alert("Deletar clicado")}
      />

      <EventoModalView
        isOpen={modalViewOpen}
        onClose={() => setModalViewOpen(false)}
        evento={eventoSelecionado}
      />
    </div>
  );
};

export default Eventos;
