export const useAdicionarEvento = () => {
    const adicionarEvento = async ({
        nome,
        equipe,
        status,
        data_inicial,
        data_final,
        cor,
        descricao
    }) => {
        const evento = {
            nome,
            equipe,
            status,
            data_inicial,
            data_final,
            cor,
            descricao,
        };

        const response = await fetch("http://localhost:5000/api/eventos/cadastrar", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(evento)
        });

        if (!response.ok) {
            const error = await response.text();
            throw new Error(`Erro ao salvar evento: ${error}`);
        }

        return await response.json();
    };

    return { adicionarEvento };
};
