import React, { useState, useEffect } from "react";
import { EventModalAdd } from "../Modals/EventoModalAdd";
import { EventoModalAction } from "../Modals/EventoModalAction";


const Eventos = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [eventos, setEventos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filtro, setFiltro] = useState("");

    const handleAbrirModal = () => setIsModalOpen(true);
    const handleFecharModal = () => setIsModalOpen(false);

    // Função para buscar eventos do backend
    const fetchEventos = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetch("http://localhost:5000/api/eventos");
            if (!response.ok) {
                const errorMsg = await response.text();
                throw new Error(errorMsg);
            }
            const data = await response.json();
            setEventos(data);
        } catch (err) {
            setError(err.message || "Erro desconhecido");
        } finally {
            setLoading(false);
        }
    };

    // Buscar eventos ao montar o componente
    useEffect(() => {
        fetchEventos();
    }, []);

    // Atualiza eventos após adicionar novo
    const handleAddEvento = (eventoNovo) => {
        setEventos(prev => [eventoNovo, ...prev]);
        handleFecharModal();
    };

    // Filtrar eventos pelo nome
    const eventosFiltrados = eventos.filter(ev =>
        ev.nome.toLowerCase().includes(filtro.toLowerCase())
    );

    const [modalActionOpen, setModalActionOpen] = useState(false);
    const [eventoSelecionado, setEventoSelecionado] = useState(null);

    const handleAbrirModalAction = (evento) => {
        setEventoSelecionado(evento);
        setModalActionOpen(true);
    };

    const handleFecharModalAction = () => {
        setModalActionOpen(false);
        setEventoSelecionado(null);
    };

    return (
        <div>
            <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", gap: "1rem", marginBottom: "1.5rem" }}>
                <span style={{ fontWeight: "bold", fontSize: "1.25rem" }}>Eventos</span>

                <input
                    type="text"
                    placeholder="Pesquisar eventos..."
                    style={{ flex: 1, maxWidth: "300px", padding: "0.5rem 1rem", border: "1px solid #ccc", borderRadius: "4px" }}
                    value={filtro}
                    onChange={e => setFiltro(e.target.value)}
                />

                <button
                    style={{ padding: "0.5rem 1.25rem", background: "#007bff", color: "#fff", border: "none", borderRadius: "4px", fontWeight: "bold", cursor: "pointer" }}
                    onClick={handleAbrirModal}
                >
                    Criar Evento
                </button>
            </div>

            <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "1.5rem" }}>
                <thead>
                    <tr>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>Nome</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>Nº Total de Equipe</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>Status</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>Data</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "center" }}></th>
                    </tr>
                </thead>
                <tbody>
                    {loading && (
                        <tr>
                            <td colSpan={5} style={{ textAlign: "center", padding: "2rem" }}>
                                Carregando eventos...
                            </td>
                        </tr>
                    )}
                    {error && (
                        <tr>
                            <td colSpan={5} style={{ textAlign: "center", color: "red", padding: "2rem" }}>
                                Erro: {error}
                            </td>
                        </tr>
                    )}
                    {!loading && !error && eventosFiltrados.length === 0 && (
                        <tr>
                            <td colSpan={5} style={{ textAlign: "center", color: "#888", padding: "2rem" }}>
                                Nenhum evento encontrado.
                            </td>
                        </tr>
                    )}
                    {!loading && !error && eventosFiltrados.map(evento => (
                        <tr key={evento.id}>
                            <td style={{ padding: "0.75rem" }}>{evento.nome}</td>
                            <td style={{ padding: "0.75rem" }}>{evento.equipe}</td>
                            <td style={{ padding: "0.75rem", textTransform: "capitalize" }}>{evento.status}</td>
                            <td style={{ padding: "0.75rem" }}>{new Date(evento.data).toLocaleDateString()}</td>
                            <td style={{ padding: "0.75rem", textAlign: "center" }}>
                                <button
                                    style={{ width: "32px", height: "32px", borderRadius: "50%", border: "1px solid #ccc", background: "#f5f5f5", cursor: "pointer" }}
                                    onClick={() => handleAbrirModalAction(evento)}
                                >
                                    ⋮
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isModalOpen && (
                <EventModalAdd
                    onClose={handleFecharModal}
                    onAddEvento={handleAddEvento}
                />
            )}

            {modalActionOpen && eventoSelecionado && (
                <EventoModalAction
                    open={modalActionOpen}
                    evento={eventoSelecionado}
                    onClose={handleFecharModalAction}
                />
            )}
        </div>
    );
};

export default Eventos;
