import React, { useState, useEffect } from 'react';
import { getEquipes, createEquipe, updateEquipe, deleteEquipe } from '../../services/equipeService';
import '../Inscricoes/Inscricoes_adaptar.css';

const Equipes = () => {
  const [equipes, setEquipes] = useState([]);
  const [currentEquipe, setCurrentEquipe] = useState({ id: null, nome: '', descricao: '', responsavel: '' });
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    fetchEquipes();
  }, []);

  const fetchEquipes = async () => {
    try {
      const data = await getEquipes();
      setEquipes(data);
    } catch (error) {
      console.error('Erro ao buscar equipes:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEquipe({ ...currentEquipe, [name]: value });
  };

  const resetForm = () => {
    setCurrentEquipe({ id: null, nome: '', descricao: '', responsavel: '' });
    setEditing(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editing) {
        await updateEquipe(currentEquipe.id, currentEquipe);
      } else {
        await createEquipe(currentEquipe);
      }
      resetForm();
      fetchEquipes();
    } catch (error) {
      console.error('Erro ao salvar equipe:', error);
    }
  };

  const handleEdit = (equipe) => {
    setCurrentEquipe({ ...equipe });
    setEditing(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta equipe?')) {
      try {
        await deleteEquipe(id);
        fetchEquipes();
      } catch (error) {
        console.error('Erro ao excluir equipe:', error);
      }
    }
  };

  return (
    <div className="equipes-container">
      <div className="equipes-header">
        <h2 className="equipes-title">Gerenciamento de Equipes</h2>
      </div>

      <form onSubmit={handleSubmit} className="equipes-form">
        <h3>{editing ? 'Editar Equipe' : 'Adicionar Nova Equipe'}</h3>
        
        <div className="equipes-form-group">
          <label htmlFor="nome">Nome da Equipe</label>
          <input 
            type="text" 
            id="nome" 
            name="nome" 
            value={currentEquipe.nome} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="descricao">Descrição</label>
          <input 
            type="text" 
            id="descricao" 
            name="descricao" 
            value={currentEquipe.descricao} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="equipes-form-group">
          <label htmlFor="responsavel">Responsável</label>
          <input 
            type="text" 
            id="responsavel" 
            name="responsavel" 
            value={currentEquipe.responsavel} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="equipes-buttons">
          <button type="submit" className="equipes-button equipes-button_primary">
            {editing ? 'Atualizar' : 'Adicionar'}
          </button>
          <button 
            type="button" 
            onClick={resetForm} 
            className="equipes-button equipes-button_warning"
          >
            Cancelar
          </button>
        </div>
      </form>

      <table className="equipes-table">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Descrição</th>
            <th>Responsável</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {equipes.length > 0 ? (
            equipes.map((equipe) => (
              <tr key={equipe.id}>
                <td>{equipe.nome}</td>
                <td>{equipe.descricao}</td>
                <td>{equipe.responsavel}</td>
                <td className="equipes-buttons">
                  <button 
                    onClick={() => handleEdit(equipe)} 
                    className="equipes-button equipes-button_warning"
                  >
                    Editar
                  </button>
                  <button 
                    onClick={() => handleDelete(equipe.id)} 
                    className="equipes-button equipes-button_danger"
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{ textAlign: 'center' }}>Nenhuma equipe encontrada</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Equipes;
