import React from 'react';
import './ProximosEventos.scss';

const ProximosEventos = ({ eventos }) => {
  // Função para formatar a data
  const formatarData = (dataString) => {
    const data = new Date(dataString);
    const dia = data.getDate().toString().padStart(2, '0');
    const mes = (data.getMonth() + 1).toString().padStart(2, '0');
    const ano = data.getFullYear();
    
    return `${dia}/${mes}/${ano}`;
  };

  return (
    <div className="proximos-eventos-container">
      <h3 className="proximos-eventos-title">Próximos Eventos</h3>
      
      {eventos.length === 0 ? (
        <div className="no-eventos">
          <p>Não há eventos programados.</p>
        </div>
      ) : (
        <div className="eventos-list">
          {eventos.map((evento, index) => (
            <div key={evento.id || index} className="evento-item">
              <div className="evento-data">
                <span className="data-valor">{formatarData(evento.data_inicial || evento.data)}</span>
              </div>
              
              <div className="evento-content">
                <h4 className="evento-titulo">{evento.nome || evento.titulo}</h4>
                <p className="evento-local">
                  <i className="fas fa-map-marker-alt"></i>
                  <span>{evento.local || "Local não informado"}</span>
                </p>
                {evento.descricao && (
                  <p className="evento-descricao">{evento.descricao}</p>
                )}
              </div>
              
              <div className="evento-status">
                <span className={`status-badge ${evento.status || 'pendente'}`}>
                  {evento.status || 'pendente'}
                </span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProximosEventos;
