import React from "react";
import "./style/EventoModals.scss";

const EventoModalView = ({ isOpen, onClose, evento }) => {
  if (!isOpen || !evento) return null;

  return (
    <div className="evento-modal-view">
      <div className="evento-modal-view-container">
        <div className="evento-modal-view-header">
          <h2>{evento.titulo || evento.nome || "Sem título"}</h2>
        </div>
        
        <div className="evento-modal-view-content">
          <div className="info-group">
            <div className="info-group-label">Data:</div>
            <div className="info-group-value">
              {evento.data_inicial && evento.data_final ? (
                `${new Date(evento.data_inicial).toLocaleDateString()} a ${new Date(evento.data_final).toLocaleDateString()}`
              ) : evento.data ? (
                new Date(evento.data).toLocaleDateString()
              ) : "Não informado"}
            </div>
          </div>
          
          <div className="info-group">
            <div className="info-group-label">Local:</div>
            <div className="info-group-value">
              {evento.local || "Não informado"}
            </div>
          </div>
          
          <div className="info-group">
            <div className="info-group-label">Status:</div>
            <div className="info-group-value">
              {evento.status || "Não informado"}
            </div>
          </div>
          
          <div className="info-group">
            <div className="info-group-label">Descrição:</div>
            <div className="info-group-value">
              {evento.descricao || "Não informado"}
            </div>
          </div>
        </div>
        
        <div className="evento-modal-view-footer">
          <button 
            className="evento-modal-view-button"
            onClick={onClose}
          >
            Fechar
          </button>
        </div>
      </div>
    </div>
  );
};

export default EventoModalView;
