import axios from 'axios';

const API_URL = 'http://localhost:3001/inscricoes-equipe';

// Buscar todas as inscrições
export const getInscricoes = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error('Erro ao buscar inscrições:', error);
    throw error;
  }
};

// Buscar inscrições de um usuário
export const getInscricoesUsuario = async (usuarioId) => {
  try {
    const response = await axios.get(`${API_URL}/usuario/${usuarioId}`);
    return response.data;
  } catch (error) {
    console.error(`Erro ao buscar inscrições do usuário ${usuarioId}:`, error);
    throw error;
