import React, { useState } from "react";
import { EventModalAdd } from "../Modals/EventoModalAdd"; // Corrigido: import nomeado

const Eventos = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleAbrirModal = () => setIsModalOpen(true);
    const handleFecharModal = () => setIsModalOpen(false);

    return (
        <div>
            <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", gap: "1rem", marginBottom: "1.5rem" }}>
                <span style={{ fontWeight: "bold", fontSize: "1.25rem" }}>Eventos</span>

                <input
                    type="text"
                    placeholder="Pesquisar eventos..."
                    style={{ flex: 1, maxWidth: "300px", padding: "0.5rem 1rem", border: "1px solid #ccc", borderRadius: "4px" }}
                />

                <button
                    style={{ padding: "0.5rem 1.25rem", background: "#007bff", color: "#fff", border: "none", borderRadius: "4px", fontWeight: "bold", cursor: "pointer" }}
                    onClick={handleAbrirModal}
                >
                    Criar Evento
                </button>
            </div>

            <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "1.5rem" }}>
                <thead>
                    <tr>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>Nome</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>NÂº Total de Equipe</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>Status</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "left" }}>Data</th>
                        <th style={{ borderBottom: "1px solid #eee", padding: "0.75rem", textAlign: "center" }}></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td colSpan={5} style={{ textAlign: "center", color: "#888", padding: "2rem" }}>
                            Nenhum evento encontrado.
                        </td>
                    </tr>
                </tbody>
            </table>

            {/* Renderiza o modal se o estado estiver true */}
            {isModalOpen && (
                <EventModalAdd
                    onClose={handleFecharModal}
                    onAddEvento={(evento) => {
                        console.log("Evento adicionado:", evento);
                       
                    }}
                />
            )}
        </div>
    );
};

export default Eventos;
