import { useEffect, useState } from "react";

export const Menu = () => {
    const [usuario, setUsuario] = useState(null);

    useEffect(() => {
        const usuarioStorage = localStorage.getItem("usuario");
        if (usuarioStorage) {
            setUsuario(JSON.parse(usuarioStorage));
        }
    }, []);

    if (!usuario) return <p>Carregando...</p>;

    return (
        <main style={{ display: "flex", minHeight: "100vh" }}>

            {/* Sidebar*/}
            <aside style={{ width: "220px", background: "#f5f5f5", padding: "2rem 1rem", display: "flex", flexDirection: "column", justifyContent: "space-between" }}>
                <nav>
                    <ul style={{ listStyle: "none", padding: 0 }}>
                        <li><a href="#dashboard">Dashboard</a></li>
                        <li><a href="#eventos">Eventos</a></li>
                        <li><a href="#equipes">Equipes</a></li>
                        li

                        <li><a href="#configuracoes">Configurações</a></li>
                    </ul>
                </nav>
                <div style={{ display: "flex", flexDirection: "column", alignItems: "center", marginTop: "2rem" }}>
                    <img src={usuario.fotoPerfil} alt="Foto de Perfil" style={{ width: 64, height: 64, borderRadius: "50%", marginBottom: "0.5rem" }} />
                    <span style={{ fontWeight: "bold" }}>{usuario.nome}</span>
                    <span style={{ fontSize: "0.9em", color: "#888" }}>{usuario.admin ? "Administrador" : "Usuário"}</span>
                </div>
                <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem", marginTop: "2rem" }}>
                    <button style={{ padding: "0.5rem", borderRadius: "4px", border: "none", background: "#e0e0e0", cursor: "pointer" }}>
                        Configurações
                    </button>
                    <button
                        style={{ padding: "0.5rem", borderRadius: "4px", border: "none", background: "#ff5252", color: "#fff", cursor: "pointer" }}
                        onClick={() => {
                            localStorage.removeItem("usuario");
                            window.location.reload();
                        }}
                    >
                        Sair
                    </button>
                </div>
            </aside>
            {/* Conteúdo principal à direita */}
            <section style={{ flex: 1, padding: "2rem" }}>a
                {/* Aqui será renderizado o conteúdo dos componentes via AJAX */}
            </section>
        </main>
    );
}
