import React, { useState, useEffect } from "react";
import axios from "axios";

import EventoModalView from "../Modals/EventoModalView";
import { EventoModalAction } from "../Modals/EventoModalAction";
import EventoModalAdd from "../Modals/EventoModalAdd";
import { useAdicionarEvento } from "../../hooks/useAdicionarEvento";

const Eventos = () => {
  const [eventos, setEventos] = useState([]);
  const [modalActionOpen, setModalActionOpen] = useState(false);
  const [modalViewOpen, setModalViewOpen] = useState(false);
  const [modalAddOpen, setModalAddOpen] = useState(false);
  const [eventoSelecionado, setEventoSelecionado] = useState(null);

  useEffect(() => {
    fetchEventos();
  }, []);

  const fetchEventos = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/eventos");
      setEventos(res.data);
    } catch (error) {
      console.error("Erro ao buscar eventos:", error);
    }
  };

  const abrirModalAction = (evento) => {
    setEventoSelecionado(evento);
    setModalActionOpen(true);
  };

  const abrirModalView = () => {
    setModalActionOpen(false);
    setModalViewOpen(true);
  };

  // Hook para adicionar evento
  const { adicionarEvento, loading: adding, error: addError } = useAdicionarEvento((novoEvento) => {
    setEventos((old) => [novoEvento, ...old]);
    setModalAddOpen(false);
  });

  const handleAdicionarEvento = (dadosEvento) => {
    adicionarEvento(dadosEvento);
  };

  return (
    <div>
      <h1>Lista de Eventos</h1>

      <button onClick={() => setModalAddOpen(true)}>Adicionar Evento</button>

      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Título</th>
            <th>Data</th>
            <th>Local</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {eventos.map((evento) => (
            <tr key={evento.id}>
              <td>{evento.nome || evento.titulo}</td>
              <td>{new Date(evento.data).toLocaleDateString()}</td>
              <td>{evento.local || "-"}</td>
              <td>{evento.status}</td>
              <td>
                <button onClick={() => abrirModalAction(evento)}>Ações</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <EventoModalAction
        open={modalActionOpen}
        onClose={() => setModalActionOpen(false)}
        onView={abrirModalView}
        onEdit={() => alert("Editar clicado")}
        onDelete={() => alert("Deletar clicado")}
      />

      <EventoModalView
        isOpen={modalViewOpen}
        onClose={() => setModalViewOpen(false)}
        evento={eventoSelecionado}
      />

      <EventoModalAdd
        isOpen={modalAddOpen}
        onClose={() => setModalAddOpen(false)}
        onAdicionar={handleAdicionarEvento}
      />

      {adding && <p>Adicionando evento...</p>}
      {addError && <p style={{ color: "red" }}>{addError}</p>}
    </div>
  );
};

export default Eventos;
